mapper.update:
update baobiao_baobiaomulu
    set processRate = #{processRate},last_preview_time = now()
where dept_id = #{deptId}
    and property = #{property}
    and countdate = #{countdate}
    and countenddate = #{countenddate}

select.if:
select distinct * from baobiao_alarmsummary_record
where AlarmReportID = #{AlarmReportID}
    and level in(1,2,3,4,5)

mapper.sql:
select t.*, case when days = 0 and hours = 0 and minutes = 0 then CONCAT(seconds , '秒') when days = 0 and hours = 0 then CONCAT(minutes , '分' , seconds , '秒') when days = 0 then CONCAT(hours ,'时' , minutes , '分' , seconds , '秒') ELSE CONCAT(days, '天', hours ,'时' , minutes , '分' , seconds , '秒') end as keeptimeShow from ( select (floor(KeepTime / 86400)) as days, (floor(KeepTime / 3600) % 24) as hours, (floor(KeepTime / 60) % 60) as minutes, (KeepTime % 60) as seconds, a.AlarmReportID, a.VehId, a.BeginTime, a.EndTime, a.MaxSpeed, a.AlarmType, a.Longitude, a.Latitude, a.Velocity, a.Angle, a.`Local`, a.Elevation, a.Limited, a.Passed, a.Time, a.KeepTime, a.Road_Name, a.Road_Level, a.Road_Limited, a.DisposeAlarmName, a.DisposeAlarmTime, a.alarmcl, a.alarmclmsg, a.alarmhdremark, a.PushState, a.verifyName, a.verifyTime, a.AnalyzeMode, a.SysPassed, ifnull( a.`status`,0) as status, case when ifnull(status,0) = 1 then '一级报警' when ifnull(status,0) = 2 then '二级报警' when ifnull(status,0) = 3 then '三级报警' end as statusShow, a.SynTime, a.plateNumber, a.plateNumber as plate, a.color, dept.dept_name as company, a.OperatType, a.EndSpeed, ROUND(ifnull(a.Distance,0)/1000,3) as Distance, a.Road_Limited2, a.IsSupplements, a.CutoffTime, a.verifyTime2, a.AlarmID, a.endLongitude, a.endLatitude, a.endRoadName, a.isRegionV, case when ifnull(isRegionV,0) = 1 then '(区域已验证)' else '' end isRegionVShow, a.AnalyzeID, a.atNocturnal, case when a.atNocturnal=1 then '夜间' else '' end atNocturnalShow, a.BaoJingType, a.ChaoSuBi, CONCAT(a.ChaoSuBi,'%') as ChaoSuBiShow, a.calctime, a.status as alarmlevel, a.alarmGuid, a.icardsign, (select accessoryphone from anbiao_vehicle_phone where is_deleted = 0 and vehid = v.id) as accessoryphone from baobiao_alarmsummary_cutofftime AS a left join (select a.id,a.dept_id from anbiao_vehicle a) v on a.VehId = v.id inner join blade_dept dept on v.dept_id = dept.id where 1=1
and cutoffTime >= CONCAT( '${beginTime}', ' 00:00:00') and cutoffTime <= CONCAT( '${endTime}', ' 23:59:59')
and dept.id = #{deptId} and Passed = 99 and AlarmType = '超速报警' and AnalyzeMode = 1
and Limited < #{roadLimited}
) as t where 1=1
