e,'%') AND xiaji.is_deleted = 0 AND xiaji.extend_type='机构'
AND shangji.id= #{jiashiyuanBaoxian.deptId}
) bd on ajb.ajb_dept_ids = bd.id left join ( select ajbm_avb_ids,IFNULL(SUM(ajbm_company_amount),0) as ajbm_company_amount,IFNULL(SUM(ajbm_basic_premium),0) as ajbm_basic_premium from anbiao_jiashiyuan_baoxian_mingxi GROUP BY ajbm_avb_ids ) cc on cc.ajbm_avb_ids = ajb.ajb_ids
ajb.ajb_delete = 0
and ajb.ajb_insured_ids= #{ajbInsuredIds}
and ajb.ajb_insured_name like concat('%', #{jiashiyuanBaoxian.ajbInsuredName}, '%')

