and ajb_insurance_period_start >= #{date}
GROUP BY ajb_dept_ids ) i ON i.ajb_dept_ids = a.id
-- 其他（企业） LEFT JOIN ( SELECT avb_dept_ids, sum(d.qitazongbaoxianjine) as qitazongbaoxianjine, sum(d.qitazongbaofeijine ) as qitazongbaofeijine FROM anbiao_dept_baoxian LEFT JOIN ( SELECT avbm_avb_ids,sum(avbm_insurance_amount) AS qitazongbaoxianjine, sum(avbm_basic_premium) AS qitazongbaofeijine FROM anbiao_dept_baoxian_mingxi WHERE avbm_risk != "安责险" and avbm_risk != "货柜险" GROUP BY avbm_avb_ids) d ON avb_ids = d.avbm_avb_ids WHERE avb_delete=0
and avb_insurance_period_start >= #{date}
GROUP BY avb_dept_ids )j on j.avb_dept_ids =a.id
where 1=1
AND a.dept_name like '%${deptName}%'
limit ${total}

