) a on a.id=avb_dept_ids INNER JOIN anbiao_organization k on a.id = k.dept_id and k.jigouleixing in('qiye','geti') LEFT JOIN ( SELECT * FROM anbiao_vehicle_baoxian_mingxi WHERE avbm_risk = "交强险" AND avbm_name = "交强险" ) d ON avb_ids = d.avbm_avb_ids LEFT JOIN ( SELECT * FROM anbiao_vehicle_baoxian_mingxi WHERE avbm_risk = "车险" AND avbm_name = "机动车损失险" ) f ON avb_ids = f.avbm_avb_ids LEFT JOIN ( SELECT * FROM anbiao_vehicle_baoxian_mingxi WHERE avbm_risk = "车险" AND avbm_name = "三者责任保险" ) g ON avb_ids = g.avbm_avb_ids LEFT JOIN ( SELECT * FROM anbiao_vehicle_baoxian_mingxi WHERE avbm_risk = "车险" AND avbm_name = "机动车车上人员责任保险(司机)" ) h ON avb_ids = h.avbm_avb_ids LEFT JOIN ( SELECT * FROM anbiao_vehicle_baoxian_mingxi WHERE avbm_risk = "车险" AND avbm_name = "机动车车上人员责任保险(乘客)" ) i ON avb_ids = i.avbm_avb_ids LEFT JOIN ( SELECT avbm_avb_ids,sum(avbm_insurance_amount) as qitajine,sum(avbm_basic_premium) as qitabaofeijine FROM anbiao_vehicle_baoxian_mingxi WHERE avbm_name != "机动车车上人员责任保险(司机)" AND avbm_name != "机动车车上人员责任保险(乘客)" AND avbm_name != "三者责任保险" AND avbm_name != "机动车损失险" AND avbm_name != "交强险" group BY avbm_avb_ids) j ON avb_ids = j.avbm_avb_ids LEFT JOIN anbiao_vehicle e ON e.id = avb_av_ids WHERE avb_delete = '0'
