mapper.select:
select avb.avb_ids, avb.avb_policy_no, avb.avb_dept_ids, avb.avb_insured_ids, avb.avb_certificate_number, avb.avb_insured_name, avb.avb_insured_contacts, avb.avb_insured_contact_number, avb.avb_insured_contact_address, avb.avb_insure_ids, avb.avb_insure_name, avb.avb_insure_contacts, avb.avb_insure_contact_number, avb.avb_insure_contact_address, avb.avb_insurance_period_start, avb.avb_insurance_period_end, avb.avb_calculation_method, avb.avb_av_ids, avb.avb_year_security_pooling_fund, avb.avb_year_premium, avb.avb_insurance_company, avb.avb_baoshi_bao_no, avb.avb_signing_date, avb.avb_total, avb.avb_replacement_price, avb.avb_bail_company_date, avb.avb_bail_headquarters_date, avb.avb_compensation_standard, avb.avb_are_accidents, avb.avb_purchase_method, avb.avb_insurance_ratio, avb.avb_permitted_seat, avb.avb_permitted_seat_driver_seat, avb.avb_permitted_seat_front_seats, avb.avb_permitted_seat_back_row_seats, avb.avb_live_seat, avb.avb_live_seat_driver_seat, avb.avb_live_seat_front_seats, avb.avb_live_seat_back_row_seats, avb.avb_seating_mode, avb.avb_driver_seat_rate, avb.avb_front_row_rate, avb.avb_back_row_rate, avb.avb_vehicle_and_vessel_tax, avb.avb_vehicle_and_vessel_tax_reduction, avb.avb_vehicle_and_vessel_tax_paid_in, avb.avb_total_premium, avb.avb_company_total_premium, avb.avb_total_planned_premium, avb.avb_totalize_premium, avb.avb_company_totalize_premium, avb.avb_approve, avb.avb_special_agreement, avb.avb_enclosure, avb.avb_delete, avb.ajb_insure_deptid,av.cheliangpaizhao,bd.dept_name,IFNULL(cc.ajbm_company_amount,0) as ajbmInsuranceAmount,IFNULL(ajbm_basic_premium,0) as ajbmBasicPremium,
case WHEN IFNULL(DATEDIFF(avb.avb_insurance_period_end,SYSDATE()),'')='' then 0 else DATEDIFF(avb.avb_insurance_period_end,SYSDATE()) end as avb_insurance_days from anbiao_vehicle_baoxian avb left join anbiao_vehicle av on av.id=avb.avb_av_ids inner join ( SELECT DISTINCT xiaji.id, xiaji.parent_id, xiaji.dept_name, xiaji.is_deleted FROM blade_dept shangji, blade_dept xiaji WHERE xiaji.tree_code LIKE CONCAT(shangji.tree_code,'%') AND xiaji.is_deleted = 0 AND xiaji.extend_type='机构'
AND shangji.id= #{vehicleBaoxian.deptId}
) bd on avb.avb_dept_ids = bd.id left join ( select avbm_avb_ids,IFNULL(SUM(avbm_insurance_amount),0) as ajbm_company_amount,IFNULL(SUM(avbm_basic_premium),0) as ajbm_basic_premium from anbiao_vehicle_baoxian_mingxi GROUP BY avbm_avb_ids ) cc on cc.avbm_avb_ids = avb.avb_ids
avb.avb_delete = 0
and avb.avb_av_ids= #{avbAvIds}
and av.cheliangpaizhao like concat('%', #{cheliangpaizhao}, '%')
and bd.dept_name like concat('%', #{deptName}, '%')

select avb.*,av.cheliangpaizhao,bd.dept_name from anbiao_vehicle_baoxian avb
left join anbiao_vehicle av on av.id=avb.avb_av_ids
LEFT JOIN blade_dept bd ON bd.id=avb.avb_dept_ids
where avb.avb_delete = 0 and avb.avb_av_ids=#{avbId}
ORDER BY avb.avb_insurance_period_end DESC limit 1 ;
