sql.if:
select * from ( select distinct a.day as date, case when IFNULL(b.vehid,'') = '' then ( select vehid from anbiao_cheliang_jiashiyuan_daily where jiashiyuanid = #{jsyId} and vstatus = 1 ) else b.vehid end as vehid, case when IFNULL(b.gvehid,'') = '' then ( select gvehid from anbiao_cheliang_jiashiyuan_daily where jiashiyuanid = #{jsyId} and gstatus = 1 ) else b.gvehid end as gvehid, b.cheliangpaizhao, b.chepaiyanse, b.jiashiyuanxingming, b.shoujihaoma, b.deptname, b.id, b.jsyid, b.`status`, b.type, b.vehimg, b.remark, b.fujianremark, b.jcrsignatrue, b.address, b.statusshow from anbiao_help_topic_time a LEFT JOIN( select a.id as vehid, a.cheliangpaizhao, a.chepaiyanse, c.jiashiyuanxingming, c.shoujihaoma, d.dept_name as deptname, b.id, b.gvehid, b.jsyid, b.`status`, b.type, b.date, b.vehimg, b.remark, b.fujianremark, b.jcrsignatrue, b.address, case when IFNULL(b.id,'') = '' then '未完成' else '已完成' end as statusshow from( select * from anbiao_vehicle where is_deleted = 0 ) a left join ( select * from anbiao_car_examine_info where left(date,10) <= left(#{date},10) and left(date,10) >= date_add(#{date}, interval -30 day) ) b on b.vehid = a.id left join anbiao_jiashiyuan c on b.jsyid = c.id and c.isdelete = 0 inner join (
) d on c.dept_id = d.id where 1=1 and b.jsyid = #{jsyId} )b on a.day = b.date where a.day <= left(#{date},10) and a.day >= date_add(#{date}, interval -30 day) )a where
a.date >= date_add(#{date}, interval -30 day)

mapper.sql:
select b.dept_name as deptName,a.*,right(a.date,2) as dateShow, v.cheliangpaizhao, j.jiashiyuanxingming from anbiao_car_examine_info a inner join (
) b on a.deptid = b.id LEFT JOIN anbiao_vehicle v on v.id = a.Vehid and v.is_deleted = 0 LEFT JOIN anbiao_jiashiyuan j on j.id = a.jsyid where a.isdelete = 0
and b.dept_name like '%${deptName}%'
and a.vehId = #{vehId}
and a.vehId in
#{id}
and year(a.date) = left(#{date},4)
and month(a.date) = month(#{date})
and month(a.date) = right(#{date},2)
and a.date >= #{beginTime} and a.date <= #{endTime}
and a.createid = #{createId}
order by left(a.date,10) asc

SELECT deptId, deptName, cheliangpaizhao, vehId, jiashiyuanxingming, COUNT(date) as count, CONCAT(#{beginTime},'至',#{endTime}) AS date, b.`status` , b.statusshow , b.jcrsignatrueshow FROM ( SELECT DISTINCT b.id as deptId, b.dept_name as deptName, v.cheliangpaizhao, v.id as vehId, COALESCE(j.jiashiyuanxingming, u.name) as jiashiyuanxingming, a.date, a.`status` as status, a.statusshow as statusshow, a.jcrsignatrueshow as jcrsignatrueshow FROM anbiao_vehicle v LEFT JOIN ( SELECT a.Vehid, a.jsyid, a.date, a.`status`, case when IFNULL(a.id,'') != '' then '1' else '0' end as statusshow, case when IFNULL(a.jcrsignatrue,'') != '' then '1' else '0' end as jcrsignatrueshow FROM anbiao_car_examine_info a WHERE a.isdelete = 0
and a.date >= #{beginTime} and a.date <= #{endTime}
) a ON v.id = a.Vehid LEFT JOIN anbiao_jiashiyuan j ON j.id = a.jsyid LEFT JOIN blade_user u ON u.id = a.jsyid INNER JOIN (
) b ON v.dept_id = b.id WHERE v.is_deleted = 0 AND v.cheliangpaizhao NOT LIKE '%挂%'
and v.cheliangpaizhao like '%${cheliangpaizhao}%'
and b.dept_name like '%${deptname}%'
) b GROUP BY deptId, deptName, cheliangpaizhao, vehId, jiashiyuanxingming

mapper.select:
SELECT DISTINCT b.dept_name AS deptName, v.cheliangpaizhao, v.id as vehId FROM(
) b INNER JOIN anbiao_vehicle v ON v.dept_id = b.id and v.cheliangpaizhao not like '%挂' where 1=1 and v.is_deleted = 0
and v.id = #{vehId}
and v.id in
#{id}

SELECT RIGHT (date, 2 ) AS date, `status` from anbiao_car_examine_info where 1=1
and year(date) = left(#{beginTime},4) and month(date) = month(#{endTime})
and vehid = #{vehId}

SELECT
    *
FROM
    `anbiao_car_examine_info`
where
        isdelete=0

        and deptid=#{deptId}


        and MONTH(date)=#{month}


        and YEAR(date)=#{year}
