mapper.sql:
SELECT DISTINCT a.id, bb.dept_name deptName , jiashiyuanxingming , case when xingbie = 1 then '男' else '女' end as xingbie, shoujihaoma , case when IFNULL(b.cheliangpaizhao,'') = '' then (SELECT v.cheliangpaizhao from anbiao_vehicle as v where v.is_deleted = 0 and v.jiashiyuanid = a.id and cheliangpaizhao not like '%挂%' limit 1) else b.cheliangpaizhao end as cheliangpaizhao, case when IFNULL(c.cheliangpaizhao,'') = '' then (SELECT v.cheliangpaizhao from anbiao_vehicle as v where v.is_deleted = 0 and v.jiashiyuanid = a.id and cheliangpaizhao like '%挂%' limit 1) else c.cheliangpaizhao end as cheliangpaizhaoguache, d.ajr_create_time as sijiruzhiriqi, e.ajw_start_date as laodonghetongkaishiriqi, e.ajw_end_date as laodonghetongjieshuriqi, case when IFNULL(e.ajw_end_date,'') = '' then '0天' when year(e.ajw_end_date) > YEAR(NOW()) then '365天' else CONCAT(TIMESTAMPDIFF(DAY,NOW(),e.ajw_end_date),'天') end as laodonghetongshengyuyouxiaoqi, shenfenzhenghao , shenfenzhengchulingriqi , shenfenzhengyouxiaoqi , case when IFNULL(shenfenzhengyouxiaoqi,'') = '' then '0天' else CONCAT(TIMESTAMPDIFF(day,NOW(),shenfenzhengyouxiaoqi),'天') end as shenfenzhengshengyuyouxiaoqi, jiashizhenghao , f.ajj_valid_period_start as jiashizhengkaishiriqi, f.ajj_valid_period_end as jiashizhengjieshuriqi, case when IFNULL(f.ajj_valid_period_end,'') = '' then '0天' else CONCAT(TIMESTAMPDIFF(day,NOW(),f.ajj_valid_period_end),'天') end as jiashizhengshengyuyouxiaoqi, g.ajc_certificate_no as congyezigezhenghao, g.ajc_initial_issuing as congyezigezhengkaishiriqi, g.ajc_valid_until as congyezigezhengjieshuriqi, case when IFNULL(g.ajc_valid_until,'') = '' then '0天' else CONCAT(TIMESTAMPDIFF(day,NOW(),g.ajc_valid_until),'天') end as congyezigezhengshengyuyouxiaoqi, h.aja_start_date as anquanshengchanzerenshuqishiriqi , case when IFNULL(h.aja_start_date,'') = '' then '0天' when year(h.aja_start_date) > YEAR(NOW()) then '365天' else CONCAT(TIMESTAMPDIFF(DAY,NOW(),DATE_ADD(h.aja_start_date, INTERVAL 1 YEAR)),'天') end as anquanshengchanzerenshushengyuyouxiaoqi, j.ajw_start_date as gangweiweixiangaozhishuqishiriqi, case when IFNULL(j.ajw_start_date,'') = '' then '0天' else CONCAT(TIMESTAMPDIFF(DAY,NOW(),DATE_ADD(j.ajw_start_date, INTERVAL 3 YEAR)),'天') end as gangweiweixiangaozhishushengyuyouxiaoqi, k.ajt_physical_examination_date as tijianbaogaoqishiriqi , case when IFNULL(k.ajt_physical_examination_date,'') = '' then '0天' when year(k.ajt_physical_examination_date) > YEAR(NOW()) then '365天' else CONCAT(TIMESTAMPDIFF(DAY,NOW(),DATE_ADD(k.ajt_physical_examination_date, INTERVAL 1 YEAR)),'天') end as tijianbaogaoshengyuyouxiaoqi, l.ajw_start_date as wuzhongdazerenshiguqishiriqi FROM anbiao_jiashiyuan a inner join (
) bb on a.dept_id = bb.id left join ( select cheliangpaizhao,b.jiashiyuanid from anbiao_vehicle a,anbiao_cheliang_jiashiyuan_daily b where a.is_deleted = 0 and a.id = b.vehid and b.vstatus = 1 -- and b.jiashiyuanid = a.jiashiyuanid and cheliangpaizhao not like '%挂%' ) b on a.id = b.jiashiyuanid left join ( select cheliangpaizhao,b.jiashiyuanid from anbiao_vehicle a,anbiao_cheliang_jiashiyuan_daily b where a.is_deleted = 0 and a.id = b.gvehid and b.gstatus = 1 -- and b.jiashiyuanid = a.jiashiyuanid and cheliangpaizhao like '%挂%'
) c on a.id = c.jiashiyuanid left join (SELECT * FROM anbiao_jiashiyuan_ruzhi WHERE ajr_delete = 0) d on a.id = d.ajr_aj_ids left join (SELECT * FROM anbiao_jiashiyuan_laodonghetong WHERE ajw_delete = 0) e on a.id = e.ajw_aj_ids left join (SELECT * FROM anbiao_jiashiyuan_jiashizheng WHERE ajj_delete = 0) f on a.id = f.ajj_aj_ids left join (SELECT * FROM anbiao_jiashiyuan_congyezigezheng WHERE ajc_delete = 0) g on a.id = g.ajc_aj_ids left join (SELECT * FROM anbiao_jiashiyuan_anquanzerenshu WHERE aja_delete = 0) h on a.id = h.aja_aj_ids left join (SELECT * FROM anbiao_jiashiyuan_weihaigaozhishu WHERE ajw_delete = 0) j on a.id = j.ajw_aj_ids left join (SELECT * FROM anbiao_jiashiyuan_tijian WHERE ajt_delete = 0) k on a.id = k.ajt_aj_ids left join (SELECT * FROM anbiao_jiashiyuan_wuzezhengming WHERE ajw_delete = 0) l on a.id = l.ajw_aj_ids WHERE 1=1 and ifnull(a.isdelete,0) = 0 order BY jiashiyuanxingming

mapper.select:
select
a.*,b.dept_name as deptName
from
anbiao_jiashiyuan a
inner join blade_dept b on a.dept_id = b.id
WHERE 1=1
    and ifnull(a.isdelete ,0) = 0
    and a.dept_id = #{deptId}

    and a.shenfenzhenghao=#{cardNo}
    and a.jiashiyuanleixing = #{jiashiyuanleixing}


    and a.jiashizhenghao=#{driverNo}
    and a.jiashiyuanleixing = #{jiashiyuanleixing}

mapper.sql:
select a.dept_name as deptName,a.jiashiyuanxingming,shoujihaoma,b.cheliangpaizhao,b.chepaiyanse,b.shiyongxingzhi,b.createtime as caozuoshijian, case when IFNULL(c.shu,0) > 0 then '已绑车' else '未绑车' end as biaoshi from ( select a.id, jiashiyuanxingming, xingbie, zhaopian, shenfenzhenghao, shoujihaoma, dept_id, shenfenzhengfujian, congyezhengfujian, jiashizhengfujian, createtime, dept_name from anbiao_jiashiyuan a inner join (
) b on a.dept_id = b.id where 1=1 and isdelete = 0 )a left join( select b.id as vehicleid,b.cheliangpaizhao,b.chepaiyanse,b.shiyongxingzhi,jiashiyuanid,a.createtime from anbiao_cheliang_jiashiyuan a, ( select id,dept_id,cheliangpaizhao,chepaiyanse,shiyongxingzhi from anbiao_vehicle where is_deleted = 0 ) b where 1=1 and a.vehid = b.id )b on a.id = b.jiashiyuanid right join( select jiashiyuanxingming,COUNT(jiashiyuanid) as shu from anbiao_cheliang_jiashiyuan a right join anbiao_jiashiyuan b on a.jiashiyuanid = b.id GROUP BY jiashiyuanxingming
HAVING COUNT(jiashiyuanid) >= 0
HAVING COUNT(jiashiyuanid) > 0
HAVING COUNT(jiashiyuanid) = 0
)c on c.jiashiyuanxingming = a.jiashiyuanxingming where 1=1 and ifnull(a.dept_name,'') != '' and (ifnull(a.jiashiyuanxingming,'') != '' and a.jiashiyuanxingming not like 'null') and ifnull(a.shoujihaoma,'') != '' and ifnull(cheliangpaizhao,'') != ''

