select.if:
SELECT rdc.id as id,a.dept_name as deptName,rdc.dept_id AS deptId,rdc.rc_id AS rcId,rdc.status AS status ,rc.yujingxiang AS yujingxiang,rc.shuoming AS shuoming,rc.yujingleixing FROM anbiao_risk_dept_configuration rdc INNER JOIN() a on a.id=rdc.dept_id LEFT JOIN anbiao_risk_configuration rc on rc.id=rdc.rc_id WHERE rdc.is_deleted = 0 and rc.is_deleted = 0
and a.dept_name like '%${deptName}%'
GROUP BY rdc.id limit ${total}

SELECT rdc.id as id,a.dept_name as deptName,rdc.dept_id AS deptId,rdc.rc_id AS rcId,rdc.status AS status ,rc.yujingxiang AS yujingxiang,rc.shuoming AS shuoming,rc.yujingleixing FROM anbiao_risk_dept_configuration rdc INNER JOIN() a on a.id=rdc.dept_id LEFT JOIN anbiao_risk_configuration rc on rc.id=rdc.rc_id WHERE rdc.is_deleted = 0 and rc.is_deleted = 0
and a.dept_name like '%${deptName}%'
GROUP BY rdc.id limit ${offsetNo},${size}
