mapper.select:
select ard_major_categories, ard_sub_category, count(1) as num from anbiao_risk_detail a inner join (
) b on a.ard_dept_ids = b.id where 1 = 1
and left(ard_discovery_date,7) = left(#{date},7) and ard_is_rectification = 0 GROUP BY ard_major_categories, ard_sub_category

select ard_major_categories, ard_sub_category, ard_discovery_date, count(1) as num from anbiao_risk_detail a inner join (
) b on a.ard_dept_ids = b.id where 1 = 1
and left(ard_discovery_date,7) = left(#{date},7) and ard_is_rectification = 0 GROUP BY ard_major_categories, ard_sub_category, ard_discovery_date

select ard_title, ard_type, ard_sub_category, ard_content, count(1) as num from anbiao_risk_detail a inner join (
) b on a.ard_dept_ids = b.id where 1 = 1
and left(ard_discovery_date,7) = left(#{date},7) and ard_sub_category = #{category} and ard_is_rectification = 0 GROUP BY ard_title, ard_type, ard_content

select ard_dept_ids, ard_ids, ard_title, ard_type, ard_discovery_date, ard_content, ard_association_value, ard_association_table, ard_association_field from anbiao_risk_detail a inner join (
) b on a.ard_dept_ids = b.id where 1 = 1
and left(ard_discovery_date,7) = left(#{date},7) and ard_sub_category = #{category} and ard_is_rectification = 0 and ard_content = #{ardContent}

