select.if:
SELECT a.driver_Id AS driverId, a.acb_maintenance_content AS acbMaintenanceContent, a.acb_repair_reason AS acbRepairReason, a.send_Date AS sendDate, a.acb_before_maintenance AS acbBeforeMaintenance, a.id AS Id, a.dept_Id AS dept_Id, c.cheliangpaizhao AS carNum, d.jiashiyuanxingming AS driverName, a.maintain_Dict_Id AS maintainDictId, a.maintenance_Dept_Name AS maintenanceDeptName, b.dept_name AS deptName FROM anbiao_cheliang_baoyangweixiu a LEFT JOIN anbiao_vehicle c ON a.vehicle_Id = c.id LEFT JOIN anbiao_jiashiyuan d ON a.driver_Id = d.id inner join (
) b on a.dept_Id = b.id a.is_Deleted = 0
AND a.send_Date between #{asiYear} and #{asiYear}
AND b.dept_name= #{deptName}
AND c.cheliangpaizhao = #{carNum}
AND a.driver_Id = #{driverId}
limit ${total}

SELECT a.driver_Id AS driverId, a.acb_maintenance_content AS acbMaintenanceContent, a.acb_repair_reason AS acbRepairReason, a.send_Date AS sendDate, a.acb_before_maintenance AS acbBeforeMaintenance, a.id AS Id, a.dept_Id AS dept_Id, c.cheliangpaizhao AS carNum, d.jiashiyuanxingming AS driverName, a.maintain_Dict_Id AS maintainDictId, a.maintenance_Dept_Name AS maintenanceDeptName, b.dept_name AS deptName FROM anbiao_cheliang_baoyangweixiu a LEFT JOIN anbiao_vehicle c ON a.vehicle_Id = c.id LEFT JOIN anbiao_jiashiyuan d ON a.driver_Id = d.id inner join (
) b on a.dept_Id = b.id a.is_Deleted = 0
AND a.send_Date between #{asiYear} and #{asiYear}
AND b.dept_name= #{deptName}
AND c.cheliangpaizhao = #{carNum}
AND a.driver_Id = #{driverId}
limit ${offsetNo},${size}

mapper.delete:
