mapper.select:
select a.zhengfuname, a.zhengfuid, a.areaname, a.cheliangshu, a.getishu, a.qiyeshu, b.baojingshu, b.chulishu, b.weichulishu, b.chulilv from( SELECT a.zhengfuname, a.zhengfuid, a.areaname, ifnull( sum( v.vehnum ), 0 ) AS cheliangshu, ifnull( sum( qy.getishu ), 0 ) AS getishu, ifnull( sum( qy.qiyeshu ), 0 ) AS qiyeshu FROM( SELECT DISTINCT b.dept_id AS qiyeid, b.dept_name AS qiyemingcheng, b.jigouleixing, b.province, b.city, b.country, a.dept_name AS zhengfuname, a.dept_id AS zhengfuid, CASE WHEN IFNULL( a.country, '' ) != '' THEN a.country WHEN IFNULL( a.city, '' ) != '' AND IFNULL( a.country, '' ) = '' THEN a.city WHEN IFNULL( a.province, '' ) != '' AND IFNULL( a.city, '' ) = '' AND IFNULL( a.country, '' ) = '' then left(a.province,2) END AS areaname FROM( SELECT a.id AS dept_id, a.parent_id, a.dept_name, tree_code, province, city, country FROM blade_dept a, anbiao_organization b WHERE a.id = b.dept_id AND jigouleixing IN ( 'xianZF', 'shiZF', 'shengZF' ) AND b.isdelete = 0
and a.id = #{deptId}
) a INNER JOIN ( SELECT dept_id, dept_name, jigouleixing, province, city, country FROM anbiao_organization WHERE 1 = 1 AND jigouleixing IN ( 'qiye', 'geti' ) AND isdelete = 0 ) b ON a.dept_id IN ( b.province, b.city, b.country ) WHERE b.province IS NOT NULL ) a LEFT JOIN ( SELECT DeptID, COUNT( DISTINCT Vehid ) AS vehnum FROM tpvehdata GROUP BY DeptID ) v ON a.qiyeid = v.DeptID LEFT JOIN ( SELECT dept_id, jigouleixing, COUNT( CASE WHEN jigouleixing = 'geti' THEN 1 ELSE NULL END ) AS getishu, COUNT( CASE WHEN jigouleixing = 'qiye' THEN 1 ELSE NULL END ) AS qiyeshu FROM anbiao_organization WHERE jigouleixing IN ( 'geti', 'qiye' ) AND isdelete = 0 GROUP BY dept_id, jigouleixing ) qy ON a.qiyeid = qy.dept_id WHERE 1 = 1 GROUP BY a.zhengfuname, a.zhengfuid, a.areaname )a LEFT JOIN( select a.areaname, a.zhengfuname, a.zhengfuid, a.baojingshu, a.chulishu, a.weichulishu, case when IFNULL(chulishu,0) = 0 or IFNULL(baojingshu,0) = 0 then '0.00%' else concat(ROUND((chulishu*1.0/baojingshu)*100,2),'%') end as chulilv from( select a.areaname, a.zhengfuname, a.zhengfuid, IFNULL(SUM(b.baojingcishu),0) as baojingshu, IFNULL(SUM(b.baojingclcishu),0) as chulishu, IFNULL(SUM(b.baojingcishu),0) - IFNULL(SUM(b.baojingclcishu),0) as weichulishu from( SELECT DISTINCT b.dept_id AS qiyeid, b.dept_name AS qiyemingcheng, b.jigouleixing, b.province, b.city, b.country, a.dept_name AS zhengfuname, a.dept_id AS zhengfuid, CASE WHEN IFNULL( a.country, '' ) != '' THEN a.country WHEN IFNULL( a.city, '' ) != '' AND IFNULL( a.country, '' ) = '' THEN a.city WHEN IFNULL( a.province, '' ) != '' AND IFNULL( a.city, '' ) = '' AND IFNULL( a.country, '' ) = '' then left(a.province,2) END AS areaname FROM( SELECT a.id AS dept_id, a.parent_id, a.dept_name, tree_code, province, city, country FROM blade_dept a, anbiao_organization b WHERE a.id = b.dept_id AND jigouleixing IN ( 'xianZF', 'shiZF', 'shengZF' ) AND b.isdelete = 0
and a.id = #{deptId}
) a INNER JOIN ( SELECT dept_id, dept_name, jigouleixing, province, city, country FROM anbiao_organization WHERE 1 = 1 AND jigouleixing IN ( 'qiye', 'geti' ) AND isdelete = 0 ) b ON a.dept_id IN ( b.province, b.city, b.country ) WHERE b.province IS NOT NULL )a inner join( select cid, company, chaosu, pilao, yejian, wushuju+budingwei as yichang, pilaoshipin, chouyan, dadianhua, fenshen, baojingcishu, baojingclcishu, vehiclecount from baobiao_alarmdailyreport where 1=1 and year(date) = year(now()) and month(date) = month(#{date}) )b on a.qiyeid = b.cid group by a.areaname, a.zhengfuname, a.zhengfuid )a )b on a.zhengfuid = b.zhengfuid

select a.zhengfuname, a.zhengfuid, a.areaname, a.cheliangshu, a.getishu, a.qiyeshu, b.baojingshu, b.chulishu, b.weichulishu, b.chulilv from( SELECT a.zhengfuname, a.zhengfuid, a.areaname, ifnull( sum( v.vehnum ), 0 ) AS cheliangshu, ifnull( sum( qy.getishu ), 0 ) AS getishu, ifnull( sum( qy.qiyeshu ), 0 ) AS qiyeshu FROM( SELECT DISTINCT b.dept_id AS qiyeid, b.dept_name AS qiyemingcheng, b.jigouleixing, b.province, b.city, b.country, a.dept_name AS zhengfuname, a.dept_id AS zhengfuid, CASE WHEN IFNULL( a.country, '' ) != '' THEN a.country WHEN IFNULL( a.city, '' ) != '' AND IFNULL( a.country, '' ) = '' THEN a.city WHEN IFNULL( a.province, '' ) != '' AND IFNULL( a.city, '' ) = '' AND IFNULL( a.country, '' ) = '' then left(a.province,2) END AS areaname FROM( SELECT a.id AS dept_id, a.parent_id, a.dept_name, tree_code, province, city, country FROM blade_dept a, anbiao_organization b WHERE a.id = b.dept_id AND jigouleixing IN ( 'xianZF', 'shiZF', 'shengZF' ) AND b.isdelete = 0
and a.parent_id = #{xiaJiDeptId}
) a INNER JOIN ( SELECT dept_id, dept_name, jigouleixing, province, city, country FROM anbiao_organization WHERE 1 = 1 AND jigouleixing IN ( 'qiye', 'geti' ) AND isdelete = 0 ) b ON a.dept_id IN ( b.province, b.city, b.country ) WHERE b.province IS NOT NULL ) a LEFT JOIN ( SELECT DeptID, COUNT( DISTINCT Vehid ) AS vehnum FROM tpvehdata GROUP BY DeptID ) v ON a.qiyeid = v.DeptID LEFT JOIN ( SELECT dept_id, jigouleixing, COUNT( CASE WHEN jigouleixing = 'geti' THEN 1 ELSE NULL END ) AS getishu, COUNT( CASE WHEN jigouleixing = 'qiye' THEN 1 ELSE NULL END ) AS qiyeshu FROM anbiao_organization WHERE jigouleixing IN ( 'geti', 'qiye' ) AND isdelete = 0 GROUP BY dept_id, jigouleixing ) qy ON a.qiyeid = qy.dept_id WHERE 1 = 1 GROUP BY a.zhengfuname, a.zhengfuid, a.areaname )a LEFT JOIN( select a.areaname, a.zhengfuname, a.zhengfuid, a.baojingshu, a.chulishu, a.weichulishu, case when IFNULL(chulishu,0) = 0 or IFNULL(baojingshu,0) = 0 then '0.00%' else concat(ROUND((chulishu*1.0/baojingshu)*100,2),'%') end as chulilv from( select a.areaname, a.zhengfuname, a.zhengfuid, IFNULL(SUM(b.baojingcishu),0) as baojingshu, IFNULL(SUM(b.baojingclcishu),0) as chulishu, IFNULL(SUM(b.baojingcishu),0) - IFNULL(SUM(b.baojingclcishu),0) as weichulishu from( SELECT DISTINCT b.dept_id AS qiyeid, b.dept_name AS qiyemingcheng, b.jigouleixing, b.province, b.city, b.country, a.dept_name AS zhengfuname, a.dept_id AS zhengfuid, CASE WHEN IFNULL( a.country, '' ) != '' THEN a.country WHEN IFNULL( a.city, '' ) != '' AND IFNULL( a.country, '' ) = '' THEN a.city WHEN IFNULL( a.province, '' ) != '' AND IFNULL( a.city, '' ) = '' AND IFNULL( a.country, '' ) = '' then left(a.province,2) END AS areaname FROM( SELECT a.id AS dept_id, a.parent_id, a.dept_name, tree_code, province, city, country FROM blade_dept a, anbiao_organization b WHERE a.id = b.dept_id AND jigouleixing IN ( 'xianZF', 'shiZF', 'shengZF' ) AND b.isdelete = 0
and a.parent_id = #{xiaJiDeptId}
) a INNER JOIN ( SELECT dept_id, dept_name, jigouleixing, province, city, country FROM anbiao_organization WHERE 1 = 1 AND jigouleixing IN ( 'qiye', 'geti' ) AND isdelete = 0 ) b ON a.dept_id IN ( b.province, b.city, b.country ) WHERE b.province IS NOT NULL )a inner join( select cid, company, chaosu, pilao, yejian, wushuju+budingwei as yichang, pilaoshipin, chouyan, dadianhua, fenshen, baojingcishu, baojingclcishu, vehiclecount from baobiao_alarmdailyreport where 1=1 and year(date) = year(now()) and month(date) = month(#{date}) )b on a.qiyeid = b.cid group by a.areaname, a.zhengfuname, a.zhengfuid )a )b on a.zhengfuid = b.zhengfuid

select * from( select a.areaname, a.zhengfuname, a.zhengfuid, a.qiyeid as deptId, a.qiyemingcheng as deptName, date_format(now(),'%Y-%m') as yue, ifnull(sum(b.baojingcishu),0) as baojingshu, IFNULL(sum(b.baojingclcishu),0) as chulishu, ifnull(sum(c.baojingcheliangshu),0) as bjcheliangshu, IFNULL(SUM(b.baojingcishu),0) - IFNULL(SUM(b.baojingclcishu),0) as weichulishu, case when IFNULL(sum(c.baojingcheliangshu),0) = 0 or IFNULL(sum(b.baojingcishu),0) = 0 then '0.00' else ROUND(IFNULL(sum(b.baojingcishu),0)*1.0/IFNULL(sum(c.baojingcheliangshu),0),2) end as danchebaojingbi, case when IFNULL(SUM(baojingclcishu),0) = 0 or IFNULL(SUM(baojingcishu),0) = 0 then '0.00%' else concat(ROUND((SUM(baojingclcishu)*1.0/SUM(baojingcishu))*100,2),'%') end as chulilv from( select qiyeid,qiyemingcheng,yunguanmingcheng as zhengfuname,yunguanid as zhengfuid,areaname from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti')
and country = #{deptId}
)a LEFT JOIN( select cid, company, replace(ltrim(replace(vehiclecount,'0',' ')),' ','0') as vehiclecount, alarmvehiclecount, chaosu, pilao, yejian, wushuju, budingwei, pilaoshipin, dadianhua, chouyan, fenshen, baojingcishu, chaosucl, pilaocl, yejiancl, wushujucl, budingweicl, pilaoshipincl, dadianhuacl, chouyancl, fenshencl, baojingclcishu from baobiao_alarmdailyreport where date_format(date,'%Y-%m') = date_format(now(),'%Y-%m') )b on b.cid = a.qiyeid LEFT JOIN( select cid, COUNT(DISTINCT VehicleId) as baojingcheliangshu from statisticcensus_alarm where 1=1 and date_format(date,'%Y-%m') = date_format(now(),'%Y-%m') and alarmcount > 0 GROUP BY cid )c on c.cid = a.qiyeid GROUP BY a.areaname, a.zhengfuname, a.zhengfuid, a.qiyeid, a.qiyemingcheng )a ORDER BY danchebaojingbi desc limit 30

select * from( select a.areaname, a.zhengfuname, a.zhengfuid, date_format(now(),'%Y-%m') as yue, ifnull(sum(b.baojingcishu),0) as baojingshu, ifnull(sum(b.baojingclcishu),0) as chulishu, ifnull(sum(c.baojingcheliangshu),0) as bjcheliangshu, IFNULL(SUM(b.baojingcishu),0) - IFNULL(SUM(b.baojingclcishu),0) as weichulishu, case when IFNULL(sum(c.baojingcheliangshu),0) = 0 or IFNULL(sum(b.baojingcishu),0) = 0 then '0.00' else ROUND(IFNULL(sum(b.baojingcishu),0)*1.0/IFNULL(sum(c.baojingcheliangshu),0),2) end as danchebaojingbi, case when IFNULL(SUM(baojingclcishu),0) = 0 or IFNULL(SUM(baojingcishu),0) = 0 then '0.00%' else concat(ROUND((SUM(baojingclcishu)*1.0/SUM(baojingcishu))*100,2),'%') end as chulilv from( select qiyeid,qiyemingcheng,yunguanmingcheng as zhengfuname,yunguanid as zhengfuid,areaname from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti')
and country = #{deptId}
)a LEFT JOIN( select cid, company, replace(ltrim(replace(vehiclecount,'0',' ')),' ','0') as vehiclecount, alarmvehiclecount, chaosu, pilao, yejian, wushuju, budingwei, pilaoshipin, dadianhua, chouyan, fenshen, baojingcishu, chaosucl, pilaocl, yejiancl, wushujucl, budingweicl, pilaoshipincl, dadianhuacl, chouyancl, fenshencl, baojingclcishu from baobiao_alarmdailyreport where date_format(date,'%Y-%m') = date_format(now(),'%Y-%m') )b on b.cid = a.qiyeid LEFT JOIN( select cid, COUNT(DISTINCT VehicleId) as baojingcheliangshu from statisticcensus_alarm where 1=1 and date_format(date,'%Y-%m') = date_format(now(),'%Y-%m') and alarmcount > 0 GROUP BY cid )c on c.cid = a.qiyeid GROUP BY a.areaname, a.zhengfuname, a.zhengfuid )a where IFNULL(a.zhengfuname,'') != '' ORDER BY danchebaojingbi desc limit 30

select * from( select a.areaname, a.zhengfuname, a.zhengfuid, date_format(now(),'%Y-%m') as yue, ifnull(sum(b.baojingcishu),0) as baojingshu, ifnull(sum(b.baojingclcishu),0) as chulishu, ifnull(sum(c.baojingcheliangshu),0) as bjcheliangshu, IFNULL(SUM(b.baojingcishu),0) - IFNULL(SUM(b.baojingclcishu),0) as weichulishu, case when IFNULL(sum(c.baojingcheliangshu),0) = 0 or IFNULL(sum(b.baojingcishu),0) = 0 then '0.00' else ROUND(IFNULL(sum(b.baojingcishu),0)*1.0/IFNULL(sum(c.baojingcheliangshu),0),2) end as danchebaojingbi, case when IFNULL(SUM(baojingclcishu),0) = 0 or IFNULL(SUM(baojingcishu),0) = 0 then '0.00%' else concat(ROUND((SUM(baojingclcishu)*1.0/SUM(baojingcishu))*100,2),'%') end as chulilv from( select qiyeid,qiyemingcheng,yunguanmingcheng as zhengfuname,yunguanid as zhengfuid,areaname from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti') and jigouleixing in ('qiye','geti')
and (city = #{xiaJiDeptId} or province = #{xiaJiDeptId})
)a LEFT JOIN( select cid, company, replace(ltrim(replace(vehiclecount,'0',' ')),' ','0') as vehiclecount, alarmvehiclecount, chaosu, pilao, yejian, wushuju, budingwei, pilaoshipin, dadianhua, chouyan, fenshen, baojingcishu, chaosucl, pilaocl, yejiancl, wushujucl, budingweicl, pilaoshipincl, dadianhuacl, chouyancl, fenshencl, baojingclcishu from baobiao_alarmdailyreport where date_format(date,'%Y-%m') = date_format(now(),'%Y-%m') )b on b.cid = a.qiyeid LEFT JOIN( select cid, COUNT(DISTINCT VehicleId) as baojingcheliangshu from statisticcensus_alarm where 1=1 and date_format(date,'%Y-%m') = date_format(now(),'%Y-%m') and alarmcount > 0 GROUP BY cid )c on c.cid = a.qiyeid GROUP BY a.areaname, a.zhengfuname, a.zhengfuid )a where IFNULL(a.zhengfuname,'') != '' ORDER BY danchebaojingbi desc limit 30

mapper.sql:
select a.areaname, a.zhengfuname, a.zhengfuid, a.qiyeid as deptId, a.qiyemingcheng as deptName, CONCAT(#{begintime},'至',#{endtime}) as date, ifnull(sum(b.vehiclecount),0) as cheliangshu, IFNULL(SUM(c.baojingcheliangshu),0) as baojingcheliangshu, ifnull(MAX(b.baojingcishu),0) as baojingzongshu, ifnull(MAX(b.baojingclcishu),0) as baojingclcishu, ifnull(MAX(b.chaosu),0) as gpschaosu, ifnull(MAX(b.pilao),0) as gpspilao, ifnull(MAX(b.yejian),0) as gpsyejian, ifnull(MAX(b.wushuju),0)+ifnull(MAX(b.budingwei),0) as gpsyichang, ifnull(MAX(b.chaosu),0)+ifnull(MAX(b.pilao),0)+ifnull(MAX(b.yejian),0)+ifnull(MAX(b.wushuju),0)+ifnull(MAX(b.budingwei),0) as gpsbaojingzongshu, ifnull(MAX(b.pilaoshipin),0) as dmspilao, ifnull(MAX(b.dadianhua),0) as dmsjiedadianhua, ifnull(MAX(b.chouyan),0) as dmschouyan, ifnull(MAX(b.fenshen),0) as dmsfenshen, ifnull(MAX(b.pilaoshipin),0)+ifnull(MAX(b.dadianhua),0)+ifnull(MAX(b.chouyan),0)+ifnull(MAX(b.fenshen),0) dmsbaojingzongshu, ifnull(MAX(b.chaosucl),0) as chaosucl, ifnull(MAX(b.pilaocl),0) as pilaocl, ifnull(MAX(b.yejiancl),0) as yejiancl, ifnull(MAX(b.wushujucl),0)+ifnull(MAX(b.budingweicl),0) as yichangcl, ifnull(MAX(b.chaosucl),0)+ifnull(MAX(b.pilaocl),0)+ifnull(MAX(b.yejiancl),0)+ifnull(MAX(b.wushujucl),0)+ifnull(MAX(b.budingweicl),0) as gpsbaojingclzongshu, ifnull(MAX(b.pilaoshipincl),0) as pilaoshipincl, ifnull(MAX(b.dadianhuacl),0) as dadianhuacl, ifnull(MAX(b.chouyancl),0) as chouyancl, ifnull(MAX(b.fenshencl),0) as fenshencl, ifnull(MAX(b.pilaoshipincl),0)+ifnull(MAX(b.dadianhuacl),0)+ifnull(MAX(b.chouyancl),0)+ifnull(MAX(b.fenshencl),0) as dmsbaojingclzongshu, case when IFNULL(SUM(c.baojingcheliangshu),0) = 0 or IFNULL(MAX(b.baojingcishu),0) = 0 then '0.00' else ROUND(IFNULL(MAX(b.baojingcishu),0)*1.0/IFNULL(SUM(c.baojingcheliangshu),0),2) end as danchebaojingbi from( select qiyeid,qiyemingcheng,yunguanmingcheng as zhengfuname,yunguanid as zhengfuid,areaname from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti')
and province = #{province}
and city = #{city}
and country = #{country}
)a LEFT JOIN( select cid, company, max(replace(ltrim(replace(vehiclecount,'0',' ')),' ','0')) vehiclecount, sum(replace(ltrim(replace(alarmvehiclecount,'0',' ')),' ','0')) alarmvehiclecount, sum(chaosu) as chaosu, sum(pilao) as pilao, sum(yejian) as yejian, sum(wushuju) as wushuju, sum(budingwei) as budingwei, sum(pilaoshipin) as pilaoshipin, sum(dadianhua) as dadianhua, sum(chouyan) as chouyan, sum(fenshen) as fenshen, sum(baojingcishu) as baojingcishu, sum(chaosucl) as chaosucl, sum(pilaocl) as pilaocl, sum(yejiancl) as yejiancl, sum(wushujucl) as wushujucl, sum(budingweicl) as budingweicl, sum(pilaoshipincl) as pilaoshipincl, sum(dadianhuacl) as dadianhuacl, sum(chouyancl) as chouyancl, sum(fenshencl) as fenshencl, sum(baojingclcishu) as baojingclcishu from baobiao_alarmdailyreport where date >= CONCAT(#{begintime},' 00:00:00') and date <= CONCAT(#{endtime},' 23:59:59') GROUP BY cid, company )b on b.cid = a.qiyeid LEFT JOIN( select cid, COUNT(DISTINCT VehicleId) as baojingcheliangshu from statisticcensus_alarm where 1=1 and date >= CONCAT(#{begintime},' 00:00:00') and date < CONCAT(#{endtime},' 23:59:59') and alarmcount > 0 GROUP BY cid )c on c.cid = a.qiyeid where 1=1
and a.qiyemingcheng = #{deptName}
GROUP BY a.areaname, a.zhengfuname, a.zhengfuid, a.qiyeid, a.qiyemingcheng

select a.*, case when IFNULL(baojingclcishu,0) = 0 or IFNULL(baojingzongshu,0) = 0 then '0.00%' else concat(ROUND((baojingclcishu*1.0/baojingzongshu)*100,2),'%') end as baojingzongcllv, case when IFNULL(gpsbaojingclzongshu,0) = 0 or IFNULL(gpsbaojingzongshu,0) = 0 then '0.00%' else concat(ROUND((gpsbaojingclzongshu*1.0/gpsbaojingzongshu)*100,2),'%') end as gpsbaojingzongcllv, case when IFNULL(dmsbaojingclzongshu,0) = 0 or IFNULL(dmsbaojingzongshu,0) = 0 then '0.00%' else concat(ROUND((dmsbaojingclzongshu*1.0/dmsbaojingzongshu)*100,2),'%') end as dmsbaojingzongcllv, case when IFNULL(chaosucl,0) = 0 or IFNULL(gpschaosu,0) = 0 then '0.00%' else concat(ROUND((chaosucl*1.0/gpschaosu)*100,2),'%') end as gpschaosucllv, case when IFNULL(pilaocl,0) = 0 or IFNULL(gpspilao,0) = 0 then '0.00%' else concat(ROUND((pilaocl*1.0/gpspilao)*100,2),'%') end as gpspilaocllv, case when IFNULL(yejiancl,0) = 0 or IFNULL(gpsyejian,0) = 0 then '0.00%' else concat(ROUND((yejiancl*1.0/gpsyejian)*100,2),'%') end as gpsyejiancllv, case when IFNULL(yichangcl,0) = 0 or IFNULL(gpsyichang,0) = 0 then '0.00%' else concat(ROUND((yichangcl*1.0/gpsyichang)*100,2),'%') end as gpsyichangcllv, case when IFNULL(pilaoshipincl,0) = 0 or IFNULL(dmspilao,0) = 0 then '0.00%' else concat(ROUND((pilaoshipincl*1.0/dmspilao)*100,2),'%') end as dmspilaocllv, case when IFNULL(dadianhuacl,0) = 0 or IFNULL(dmsjiedadianhua,0) = 0 then '0.00%' else concat(ROUND((dadianhuacl*1.0/dmsjiedadianhua)*100,2),'%') end as dmsjiedadianhuacllv, case when IFNULL(chouyancl,0) = 0 or IFNULL(dmschouyan,0) = 0 then '0.00%' else concat(ROUND((chouyancl*1.0/dmschouyan)*100,2),'%') end as dmschouyancllv, case when IFNULL(fenshencl,0) = 0 or IFNULL(dmsfenshen,0) = 0 then '0.00%' else concat(ROUND((fenshencl*1.0/dmsfenshen)*100,2),'%') end as dmsfenshencllv from( select a.areaname, a.zhengfuname, a.zhengfuid, CONCAT(#{begintime},'至',#{endtime}) as date,
IFNULL(SUM(b.baojingcheliangshu),0) as cheliangshu, ifnull(sum(b.baojingcishu),0) as baojingzongshu, ifnull(sum(b.baojingclcishu),0) as baojingclcishu, ifnull(sum(b.chaosu),0) as gpschaosu, ifnull(sum(b.pilao),0) as gpspilao, ifnull(sum(b.yejian),0) as gpsyejian, ifnull(sum(b.wushuju),0)+ifnull(sum(b.budingwei),0) as gpsyichang, ifnull(sum(b.chaosu),0)+ifnull(sum(b.pilao),0)+ifnull(sum(b.yejian),0)+ifnull(sum(b.wushuju),0)+ifnull(sum(b.budingwei),0) as gpsbaojingzongshu, ifnull(sum(b.pilaoshipin),0) as dmspilao, ifnull(sum(b.dadianhua),0) as dmsjiedadianhua, ifnull(sum(b.chouyan),0) as dmschouyan, ifnull(sum(b.fenshen),0) as dmsfenshen, ifnull(sum(b.pilaoshipin),0)+ifnull(sum(b.dadianhua),0)+ifnull(sum(b.chouyan),0)+ifnull(sum(b.fenshen),0) dmsbaojingzongshu, ifnull(sum(b.chaosucl),0) as chaosucl, ifnull(sum(b.pilaocl),0) as pilaocl, ifnull(sum(b.yejiancl),0) as yejiancl, ifnull(sum(b.wushujucl),0)+ifnull(sum(b.budingweicl),0) as yichangcl, ifnull(sum(b.chaosucl),0)+ifnull(sum(b.pilaocl),0)+ifnull(sum(b.yejiancl),0)+ifnull(sum(b.wushujucl),0)+ifnull(sum(b.budingweicl),0) as gpsbaojingclzongshu, ifnull(sum(b.pilaoshipincl),0) as pilaoshipincl, ifnull(sum(b.dadianhuacl),0) as dadianhuacl, ifnull(sum(b.chouyancl),0) as chouyancl, ifnull(sum(b.fenshencl),0) as fenshencl, ifnull(sum(b.pilaoshipincl),0)+ifnull(sum(b.dadianhuacl),0)+ifnull(sum(b.chouyancl),0)+ifnull(sum(b.fenshencl),0) as dmsbaojingclzongshu from( select qiyeid,qiyemingcheng,yunguanmingcheng as zhengfuname,yunguanid as zhengfuid,areaname from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti')
and (city = #{deptId} or province = #{deptId})
and country = #{xiaJiDeptId}
)a LEFT JOIN( select a.cid, company, max(replace(ltrim(replace(vehiclecount,'0',' ')),' ','0')) vehiclecount, sum(chaosu) as chaosu, sum(pilao) as pilao, sum(yejian) as yejian, sum(wushuju) as wushuju, sum(budingwei) as budingwei, sum(pilaoshipin) as pilaoshipin, sum(dadianhua) as dadianhua, sum(chouyan) as chouyan, sum(fenshen) as fenshen, sum(baojingcishu) as baojingcishu, sum(chaosucl) as chaosucl, sum(pilaocl) as pilaocl, sum(yejiancl) as yejiancl, sum(wushujucl) as wushujucl, sum(budingweicl) as budingweicl, sum(pilaoshipincl) as pilaoshipincl, sum(dadianhuacl) as dadianhuacl, sum(chouyancl) as chouyancl, sum(fenshencl) as fenshencl, sum(baojingclcishu) as baojingclcishu, MAX(baojingcheliangshu) as baojingcheliangshu from baobiao_alarmdailyreport a left join ( select cid, COUNT(DISTINCT VehicleId) as baojingcheliangshu from statisticcensus where 1=1 and date >= CONCAT(#{begintime},' 00:00:00') and date < CONCAT(#{endtime},' 23:59:59') and IFNULL(IFNULL(AlarmCount,0)+IFNULL(dsm,0),0) > 0 GROUP BY cid ) b on a.cid = b.cid where 1=1 and date >= CONCAT(#{begintime},' 00:00:00') and date < CONCAT(#{endtime},' 23:59:59') GROUP BY a.cid, company )b on b.cid = a.qiyeid GROUP BY a.areaname, a.zhengfuname, a.zhengfuid )a order by a.baojingzongshu desc

select a.*, case when IFNULL(baojingclcishu,0) = 0 or IFNULL(baojingzongshu,0) = 0 then '0.00%' else concat(ROUND((baojingclcishu*1.0/baojingzongshu)*100,2),'%') end as baojingzongcllv, case when IFNULL(gpsbaojingclzongshu,0) = 0 or IFNULL(gpsbaojingzongshu,0) = 0 then '0.00%' else concat(ROUND((gpsbaojingclzongshu*1.0/gpsbaojingzongshu)*100,2),'%') end as gpsbaojingzongcllv, case when IFNULL(dmsbaojingclzongshu,0) = 0 or IFNULL(dmsbaojingzongshu,0) = 0 then '0.00%' else concat(ROUND((dmsbaojingclzongshu*1.0/dmsbaojingzongshu)*100,2),'%') end as dmsbaojingzongcllv, case when IFNULL(chaosucl,0) = 0 or IFNULL(gpschaosu,0) = 0 then '0.00%' else concat(ROUND((chaosucl*1.0/gpschaosu)*100,2),'%') end as gpschaosucllv, case when IFNULL(pilaocl,0) = 0 or IFNULL(gpspilao,0) = 0 then '0.00%' else concat(ROUND((pilaocl*1.0/gpspilao)*100,2),'%') end as gpspilaocllv, case when IFNULL(yejiancl,0) = 0 or IFNULL(gpsyejian,0) = 0 then '0.00%' else concat(ROUND((yejiancl*1.0/gpsyejian)*100,2),'%') end as gpsyejiancllv, case when IFNULL(yichangcl,0) = 0 or IFNULL(gpsyichang,0) = 0 then '0.00%' else concat(ROUND((yichangcl*1.0/gpsyichang)*100,2),'%') end as gpsyichangcllv, case when IFNULL(pilaoshipincl,0) = 0 or IFNULL(dmspilao,0) = 0 then '0.00%' else concat(ROUND((pilaoshipincl*1.0/dmspilao)*100,2),'%') end as dmspilaocllv, case when IFNULL(dadianhuacl,0) = 0 or IFNULL(dmsjiedadianhua,0) = 0 then '0.00%' else concat(ROUND((dadianhuacl*1.0/dmsjiedadianhua)*100,2),'%') end as dmsjiedadianhuacllv, case when IFNULL(chouyancl,0) = 0 or IFNULL(dmschouyan,0) = 0 then '0.00%' else concat(ROUND((chouyancl*1.0/dmschouyan)*100,2),'%') end as dmschouyancllv, case when IFNULL(fenshencl,0) = 0 or IFNULL(dmsfenshen,0) = 0 then '0.00%' else concat(ROUND((fenshencl*1.0/dmsfenshen)*100,2),'%') end as dmsfenshencllv from( select a.areaname, a.zhengfuname, a.zhengfuid, a.qiyeid as deptId, a.qiyemingcheng as deptName, CONCAT(#{begintime},'至',#{endtime}) as date, IFNULL(SUM(b.baojingcheliangshu),0) as cheliangshu, ifnull(sum(b.baojingcishu),0) as baojingzongshu, ifnull(sum(b.baojingclcishu),0) as baojingclcishu, ifnull(sum(b.chaosu),0) as gpschaosu, ifnull(sum(b.pilao),0) as gpspilao, ifnull(sum(b.yejian),0) as gpsyejian, ifnull(sum(b.wushuju),0)+ifnull(sum(b.budingwei),0) as gpsyichang, ifnull(sum(b.chaosu),0)+ifnull(sum(b.pilao),0)+ifnull(sum(b.yejian),0)+ifnull(sum(b.wushuju),0)+ifnull(sum(b.budingwei),0) as gpsbaojingzongshu, ifnull(sum(b.pilaoshipin),0) as dmspilao, ifnull(sum(b.dadianhua),0) as dmsjiedadianhua, ifnull(sum(b.chouyan),0) as dmschouyan, ifnull(sum(b.fenshen),0) as dmsfenshen, ifnull(sum(b.pilaoshipin),0)+ifnull(sum(b.dadianhua),0)+ifnull(sum(b.chouyan),0)+ifnull(sum(b.fenshen),0) dmsbaojingzongshu, ifnull(sum(b.chaosucl),0) as chaosucl, ifnull(sum(b.pilaocl),0) as pilaocl, ifnull(sum(b.yejiancl),0) as yejiancl, ifnull(sum(b.wushujucl),0)+ifnull(sum(b.budingweicl),0) as yichangcl, ifnull(sum(b.chaosucl),0)+ifnull(sum(b.pilaocl),0)+ifnull(sum(b.yejiancl),0)+ifnull(sum(b.wushujucl),0)+ifnull(sum(b.budingweicl),0) as gpsbaojingclzongshu, ifnull(sum(b.pilaoshipincl),0) as pilaoshipincl, ifnull(sum(b.dadianhuacl),0) as dadianhuacl, ifnull(sum(b.chouyancl),0) as chouyancl, ifnull(sum(b.fenshencl),0) as fenshencl, ifnull(sum(b.pilaoshipincl),0)+ifnull(sum(b.dadianhuacl),0)+ifnull(sum(b.chouyancl),0)+ifnull(sum(b.fenshencl),0) as dmsbaojingclzongshu from( select qiyeid,qiyemingcheng,yunguanmingcheng as zhengfuname,yunguanid as zhengfuid,areaname from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti')
and (city = #{deptId} or province = #{deptId})
and country = #{xiaJiDeptId}
)a LEFT JOIN( select a.cid, company, max(replace(ltrim(replace(vehiclecount,'0',' ')),' ','0')) vehiclecount, sum(chaosu) as chaosu, sum(pilao) as pilao, sum(yejian) as yejian, sum(wushuju) as wushuju, sum(budingwei) as budingwei, sum(pilaoshipin) as pilaoshipin, sum(dadianhua) as dadianhua, sum(chouyan) as chouyan, sum(fenshen) as fenshen, sum(baojingcishu) as baojingcishu, sum(chaosucl) as chaosucl, sum(pilaocl) as pilaocl, sum(yejiancl) as yejiancl, sum(wushujucl) as wushujucl, sum(budingweicl) as budingweicl, sum(pilaoshipincl) as pilaoshipincl, sum(dadianhuacl) as dadianhuacl, sum(chouyancl) as chouyancl, sum(fenshencl) as fenshencl, sum(baojingclcishu) as baojingclcishu, MAX(baojingcheliangshu) as baojingcheliangshu from baobiao_alarmdailyreport a left join ( select cid, COUNT(DISTINCT VehicleId) as baojingcheliangshu from statisticcensus where date >= CONCAT(#{begintime},' 00:00:00') and date < CONCAT(#{endtime},' 23:59:59') and cid in( select qiyeid from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti')
and (city = #{deptId} or province = #{deptId})
and country = #{xiaJiDeptId}
) and IFNULL(IFNULL(AlarmCount,0)+IFNULL(dsm,0),0) > 0 GROUP BY cid ) b on a.cid = b.cid where 1=1 and a.date >= CONCAT(#{begintime},' 00:00:00') and a.date < CONCAT(#{endtime},' 23:59:59') GROUP BY a.cid, company )b on b.cid = a.qiyeid GROUP BY a.areaname, a.zhengfuname, a.zhengfuid, a.qiyeid, a.qiyemingcheng )a where 1=1
and a.deptName like '%${deptName}%'
order by a.baojingzongshu desc

select case when days = 0 and hours = 0 and minutes = 0 then CONCAT(seconds , '秒') when days = 0 and hours = 0 then CONCAT(minutes , '分' , seconds , '秒') when days = 0 then CONCAT(hours ,'时' , minutes , '分' , seconds , '秒') ELSE CONCAT(days, '天', hours ,'时' , minutes , '分' , seconds , '秒') end as keeptimeShow, a.* from ( SELECT (floor(KeepTime / 86400)) as days, (floor(KeepTime / 3600) % 24) as hours, (floor(KeepTime / 60) % 60) as minutes, (KeepTime % 60) as seconds, a.AlarmReportID, a.VehId, a.BeginTime, a.EndTime, a.MaxSpeed, a.AlarmType as alarmtype, a.Longitude, a.Latitude, a.Velocity, a.Limited as limited, a.Road_Name, a.Road_Level, a.Road_Limited, a.DisposeAlarmName, a.DisposeAlarmTime, a.alarmcl, a.alarmclmsg, a.plateNumber as plate, a.color, a.company, a.OperatType, a.EndSpeed, ROUND(ifnull(a.Distance,0)/1000,3) as Distance, a.AlarmID, a.isRegionV, case when ifnull(isRegionV,0) = 1 then '(区域已验证)' else '' end isRegionVShow, a.BaoJingType, a.ChaoSuBi, CONCAT(a.ChaoSuBi,'%') as ChaoSuBiShow, case when ifnull(b.chulizhuangtai,0) = 1 and remark=1 then '已处理' when ifnull(b.chulizhuangtai,0) = 2 and remark=1 THEN '超时处理' when ifnull(b.chulizhuangtai,0) = 3 and remark=1 THEN '已处理未通过' else '未处理' end as chulizhuangtai, case when ifnull(b.chulizhuangtai,0) = 1 and remark=0 then '已申诉' when ifnull(b.chulizhuangtai,0) = 2 and remark=0 THEN '超时申诉' when ifnull(b.chulizhuangtai,0) = 3 and remark=0 THEN '已申诉未通过' else '未申诉' end as shensuzhuangtai, b.chulixingshi, b.chulimiaoshu, b.chulirenid, b.chuliren, b.chulishijian, b.fujian, b.beizhu, b.remark FROM baobiao_alarmsummary_cutofftime AS a left join (select id,dept_id from anbiao_vehicle) v on a.VehId = v.id left JOIN baobiao_alarmhandleresult AS b ON a.AlarmReportID = b.baojingid and v.dept_id = b.qiyeid and (a.AlarmType=b.baojingleixing or b.baojingleixing = '异常车辆报警') and ifnull(b.is_deleted,0) = 0 where 1=1
and date_format(cutofftime,'%Y-%m-%d') >= date_format(#{begintime},'%Y-%m-%d') and date_format(cutofftime,'%Y-%m-%d') <= date_format(#{endtime},'%Y-%m-%d')
and passed = 100 and AnalyzeMode = 1
and AlarmType in ('无数据报警','不定位报警')
and AlarmType = #{alarmtype}
and company = #{deptName}
)a

SELECT a.id, a.id as alarmReportID, a.AlarmNumber, a.AlarmID, a.VehId, a.plate, a.color, a.FlagState, case when ifnull(a.AlarmType,'') = '疲劳驾驶报警' then '生理疲劳报警' else a.AlarmType end as alarmtype, a.Alarmlevel as alarmlevel, a.Velocity as velocity, a.High, a.Lon as longitude, a.Lat as latitude, a.GpsTime as begintime, a.VehStatus as vehStatus, a.A_Time as aTime, a.OwnNo, a.company, a.IsVideo, a.IsPicture, a.RoadName as roadName, a.OperatType as operatType, a.stateEx, case when ifnull(b.chulizhuangtai,0) = 1 and remark=1 then '已处理' when ifnull(b.chulizhuangtai,0) = 2 and remark=1 THEN '超时处理' when ifnull(b.chulizhuangtai,0) = 3 and remark=1 THEN '已处理未通过' else '未处理' end as chulizhuangtai, case when ifnull(b.chulizhuangtai,0) = 1 and remark=0 then '已申诉' when ifnull(b.chulizhuangtai,0) = 2 and remark=0 THEN '超时申诉' when ifnull(b.chulizhuangtai,0) = 3 and remark=0 THEN '已申诉未通过' else '未申诉' end as shensuzhuangtai, b.chulixingshi, b.chulimiaoshu, b.chulirenid, b.chuliren, b.chulishijian, b.fujian, b.beizhu, b.remark FROM baobiao_driverbehavior AS a left JOIN baobiao_alarmhandleresult AS b ON a.id = b.baojingid and a.AlarmType = b.baojingleixing AND remark = 1 and ifnull(b.is_deleted,0) = 0 where 1=1 and stateEx = '核定报警'
and date_format(a.GpsTime,'%Y-%m-%d') >= date_format(#{begintime},'%Y-%m-%d') and date_format(a.GpsTime,'%Y-%m-%d') <= date_format(#{endtime},'%Y-%m-%d')
and a.company = #{deptName}
and a.AlarmType = #{alarmtype}

select a.AlarmReportID as alarmReportID, a.company, a.VehId, a.plate, a.color, a.operatType, a.AlarmType as alarmtype, DATE_FORMAT(a.BeginTime,"%Y-%m-%d %H:%i:%s") as beginTime, DATE_FORMAT(a.EndTime,"%Y-%m-%d %H:%i:%s") as endTime, a.calctime, a.status as alarmlevel, a.alarmGuid, a.icardsign, a.Distance as distance, a.roadLimited, case when days = 0 and hours = 0 and minutes = 0 then CONCAT(seconds , '秒') when days = 0 and hours = 0 then CONCAT(minutes , '分' , seconds , '秒') when days = 0 then CONCAT(hours ,'时' , minutes , '分' , seconds , '秒') ELSE CONCAT(days, '天', hours ,'时' , minutes , '分' , seconds , '秒') end as keeptimeShow, a.Velocity as velocity, a.Longitude as longitude, a.Latitude as latitude, a.RoadName as roadName, case when ifnull(a.chulizhuangtai,0) = 1 and a.remark=1 then '已处理' when ifnull(a.chulizhuangtai,0) = 2 and a.remark=1 THEN '超时处理' when ifnull(a.chulizhuangtai,0) = 3 and a.remark=1 THEN '已处理未通过' else '未处理' end as chulizhuangtai, case when ifnull(a.chulizhuangtai,0) = 1 and a.remark=0 then '已申诉' when ifnull(a.chulizhuangtai,0) = 2 and a.remark=0 THEN '超时申诉' when ifnull(a.chulizhuangtai,0) = 3 and a.remark=0 THEN '已申诉未通过' else '未申诉' end as shensuzhuangtai, a.chulixingshi, a.chulimiaoshu, a.chulirenid, a.chuliren, DATE_FORMAT(a.chulishijian,"%Y-%m-%d %H:%i:%s") as chulishijian, a.fujian, a.beizhu, CONCAT(ChaoSuBi,'%') as ChaoSuBiShow, remark, maxSpeed from ( SELECT a.VehId, (floor(KeepTime / 86400)) as days, (floor(KeepTime / 3600) % 24) as hours, (floor(KeepTime / 60) % 60) as minutes, (KeepTime % 60) as seconds, a.AlarmReportID, a.BeginTime, a.EndTime, a.MaxSpeed as maxSpeed, a.AlarmType as alarmtype, a.Longitude, a.Latitude, a.Velocity, a.Limited as limited, a.Road_Name as RoadName, a.Road_Level as RoadLevel, a.Road_Limited as RoadLimited, a.plateNumber as plate, a.color, a.company, a.OperatType as operatType, a.EndSpeed, ROUND(ifnull(a.Distance,0)/1000,3) as Distance, ifnull(a.calctime,'') as calctime, case when ifnull(a.status,'') = '1' then '一级报警' when ifnull(a.status,'') = '2' then '二级报警' when ifnull(a.status,'') = '3' then '三级报警' end as status, ifnull(a.icardsign,'') as icardsign, ifnull(a.AlarmGuid,'') as alarmGuid, b.chulizhuangtai, b.chulixingshi, b.chulimiaoshu, b.chulirenid, b.chuliren, b.chulishijian, b.fujian, b.beizhu, a.ChaoSuBi, b.remark FROM baobiao_alarmsummary_cutofftime AS a left join (select id,dept_id from anbiao_vehicle) v on a.VehId = v.id left JOIN baobiao_alarmhandleresult AS b ON a.AlarmReportID = b.baojingid and v.dept_id = b.qiyeid and (a.AlarmType=b.baojingleixing or b.baojingleixing = '异常车辆报警') and ifnull(b.is_deleted,0) = 0 where 1=1
and date_format(cutofftime,'%Y-%m-%d') >= date_format(#{begintime},'%Y-%m-%d') and date_format(cutofftime,'%Y-%m-%d') <= date_format(#{endtime},'%Y-%m-%d')
and passed = 100 and AnalyzeMode = 1
and a.company in ( select qiyemingcheng from baobiao_zhengfu_qiye where 1=1
and province = #{deptId}
and city = #{deptId}
and country = #{deptId}
)
and a.company = #{deptName}
and AlarmType in ('超速报警','疲劳驾驶报警','夜间行驶报警','无数据报警','不定位报警')
and AlarmType in ('无数据报警','不定位报警')
and AlarmType = #{alarmtype}
and plateNumber = #{cheliangpaizhao}
)a union SELECT a.AlarmNumber as alarmReportID, a.company, a.VehId, a.plate, a.color, a.OperatType as operatType, case when ifnull(a.AlarmType,'') = '疲劳驾驶报警' then '生理疲劳报警' else a.AlarmType end as alarmtype, DATE_FORMAT(a.GpsTime,"%Y-%m-%d %H:%i:%s") as beginTime, DATE_FORMAT(a.GpsTime,"%Y-%m-%d %H:%i:%s") as endTime, '' as calctime, ifnull(a.Alarmlevel,'') as alarmlevel, ifnull(a.AlarmGuid,'') as alarmGuid, ifnull(a.icardsign,'') as icardsign, ifnull(a.distance,0) as distance, '0' as roadLimited, '3分钟' as keeptimeshow, a.Velocity as velocity, a.Lon as longitude, a.Lat as latitude, a.RoadName as roadName, case when ifnull(b.chulizhuangtai,0) = 1 and b.remark=1 then '已处理' when ifnull(b.chulizhuangtai,0) = 2 and b.remark=1 THEN '超时处理' when ifnull(b.chulizhuangtai,0) = 3 and b.remark=1 THEN '已处理未通过' else '未处理' end as chulizhuangtai, case when ifnull(b.chulizhuangtai,0) = 1 and b.remark=0 then '已申诉' when ifnull(b.chulizhuangtai,0) = 2 and b.remark=0 THEN '超时申诉' when ifnull(b.chulizhuangtai,0) = 3 and b.remark=0 THEN '已申诉未通过' else '未申诉' end as shensuzhuangtai, b.chulixingshi, b.chulimiaoshu, b.chulirenid, b.chuliren, b.chulishijian, b.fujian, b.beizhu, '' as ChaoSuBi, b.remark, '' as maxSpeed FROM baobiao_driverbehavior AS a left JOIN baobiao_alarmhandleresult AS b ON a.id = b.baojingid and a.AlarmType = b.baojingleixing and ifnull(b.is_deleted,0) = 0 where 1=1 and stateEx = '核定报警'
and plate = #{cheliangpaizhao}
and AlarmType in ('接打电话报警','疲劳驾驶报警','分神驾驶报警','抽烟报警')
and AlarmType = #{alarmtype}
and date_format(GpsTime,'%Y-%m-%d') >= date_format(#{begintime},'%Y-%m-%d') and date_format(GpsTime,'%Y-%m-%d') <= date_format(#{endtime},'%Y-%m-%d')
and a.company in ( select qiyemingcheng from baobiao_zhengfu_qiye where 1=1
and province = #{deptId}
and city = #{deptId}
and country = #{deptId}
)
and a.company = #{deptName}

