select a.qiyeid as deptId, a.qiyemingcheng as company, b.cheliangpaizhao, b.chepaiyanse, b.shiyongxingzhi, b.LocatePositionCount as DaysOnline, b.DriftPositionRate, b.IntactPositionRate, b.QualifiedPositionRate, CONCAT(#{begintime},'至',#{endtime}) as date from ( select qiyeid,qiyemingcheng,yunguanmingcheng as zhengfuname,yunguanid as zhengfuid,areaname from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti')
