and country = #{xiaJiDeptId}
)a inner join( select a.dept_id, a.cheliangpaizhao, a.chepaiyanse, a.shiyongxingzhi, IFNULL(SUM(b.LocatePositionCount),0) LocatePositionCount, case when IFNULL(SUM(b.DriftPositionRate),0) = 0 then '0.00%' else concat(ROUND(SUM(b.DriftPositionRate)*100,2),'%') end as DriftPositionRate, case when IFNULL(SUM(b.IntactPositionRate),0) = 0 then '0.00%' else concat(ROUND(avg(b.IntactPositionRate)*100,2),'%') end as IntactPositionRate, case when IFNULL(SUM(b.PositionCount),0) = 0 or IFNULL(SUM(b.QualifiedPositionCount),0) = 0 then '0.00%' else concat(ROUND((SUM(b.QualifiedPositionCount)*1.0/SUM(b.PositionCount))*100,2),'%') end as QualifiedPositionRate from anbiao_vehicle a inner join( select vehicleId, case when IFNULL(sum(LocatePositionCount),0) > 0 then 1 else 0 end as LocatePositionCount, sum(DriftPositionRate) as DriftPositionRate, sum(IntactPositionRate) as IntactPositionRate, sum(QualifiedPositionCount) as QualifiedPositionCount, sum(PositionCount) as PositionCount from statisticcensus where 1=1 and date >= CONCAT(#{begintime},' 00:00:00') and date < CONCAT(#{endtime},' 23:59:59') GROUP BY LEFT(date,10), vehicleId )b on a.id = b.vehicleId group by a.dept_id, a.cheliangpaizhao, a.chepaiyanse, a.shiyongxingzhi )b on a.qiyeid = b.dept_id

