mapper.sql:
select a.areaname, a.zhengfuname, a.zhengfuid, b.operatorName, case when IFNULL(b.date,'') = '' then '2022-07-21' else b.date end as date, vehicleCount, IFNULL(b.upLineCount,0) as upLineCount, IFNULL(b.offLineCount,0) as offLineCount, case when IFNULL(b.onlineRate,'') = '' then '100.00%' else b.onlineRate end as onlineRate, IFNULL(b.locateVehicleCount,0) as locateCount, case when IFNULL(b.locaterate,'') = '' then '100.00%' else b.locaterate end as locateRate, case when IFNULL(b.DriftPositionRate,'') = '' then '100.00%' else b.DriftPositionRate end as DriftPositionRate, case when IFNULL(b.IntactPositionRate,'') = '' then '100.00%' else b.IntactPositionRate end as IntactPositionRate, case when IFNULL(b.QualifiedPositionRate,'') = '' then '100.00%' else b.QualifiedPositionRate end as QualifiedPositionRate from( select qiyeid,qiyemingcheng,yunguanmingcheng as zhengfuname,yunguanid as zhengfuid,areaname from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti')
and province = #{province}
and city = #{city}
and country = #{country}
)a INNER JOIN( select deptId, operatorName, date, replace(ltrim(replace(vehiclecount,'0',' ')),' ','0') as vehiclecount, upLineCount, offLineCount, case when IFNULL(vehiclecount,0) = 0 or IFNULL(UpLineCount,0) = 0 then '0.00%' else concat(ROUND((UpLineCount*1.0/vehiclecount)*100,2),'%') end as onlineRate, IFNULL(locateVehicleCount,0) as locateVehicleCount, case when IFNULL(vehiclecount,0) = 0 or IFNULL(locateVehicleCount,0) = 0 then '0.00%' else concat(ROUND((locateVehicleCount*1.0/vehiclecount)*100,2),'%') end as locateRate, concat(ROUND(DriftPositionRate*100,2),'%') as DriftPositionRate, concat(ROUND(IntactPositionRate,2),'%') as IntactPositionRate, case when ifnull(QualifiedPositionRate,'') = '' then '100.00%' else concat(ROUND(QualifiedPositionRate,2),'%') end as QualifiedPositionRate from baobiao_operator_monitor where 1=1 and date >= CONCAT(#{begintime},' 00:00:00') and date < CONCAT(#{endtime},' 23:59:59') )b on b.deptId = a.qiyeid

sql.if:
select *, ROUND(IFNULL(uplinerateScore,0) +IFNULL(DriftPositionRateScore,0) +IFNULL(IntactPositionRateScore,0) +IFNULL(QualifiedPositionRateScore,0) +IFNULL(yunzhengjichuScore,0) +IFNULL(yuqizhuxiaoScore,0) +IFNULL(chaoqinianshenScore,0) +IFNULL(zaiyunxingchelianglixianScore,0) +IFNULL(chaosuScore,0) +IFNULL(pilaotimesScore,0),2) as countScore from( select DISTINCT a.zhengfuname, a.zhengfuid, a.areaname, a.qiyeid, a.qiyemingcheng, b.date, '100.00%' as yunzhengjichuRate, '5.00' as yunzhengjichuScore, '100.00%' as yuqizhuxiaoRate, '5.00' as yuqizhuxiaoScore, '100.00%' as chaoqinianshenRate, '10.00' as chaoqinianshenScore, CONCAT(b.uplinerate,'%') as onlineRate, case when ifnull(b.uplinerateScore,0) > 15 then 15.00 else ifnull(b.uplinerateScore,0) end as uplinerateScore, CONCAT(b.IntactPositionRate,'%') as IntactPositionRate, case when ifnull(b.IntactPositionRateScore,0) > 10 then 10.00 else ifnull(b.IntactPositionRateScore,0) end as IntactPositionRateScore, CONCAT(b.QualifiedPositionRate,'%') as QualifiedPositionRate, case when ifnull(b.QualifiedPositionRateScore,0) > 10 then 10.00 else ifnull(b.QualifiedPositionRateScore,0) end as QualifiedPositionRateScore, CONCAT(b.DriftPositionRate,'%') as DriftPositionRate, case when ifnull(b.DriftPositionRateScore,0) > 10 then 10.00 else ifnull(b.DriftPositionRateScore,0) end as DriftPositionRateScore, chaosualarm, pilaotimes, case when ifnull(b.chaosuScore,0) > 10 then 10.00 else ifnull(b.chaosuScore,0) end as chaosuScore, case when ifnull(b.pilaotimesScore,0) > 10 then 10.00 else ifnull(b.pilaotimesScore,0) end as pilaotimesScore, '15.00' as zaiyunxingchelianglixianScore from( select qiyeid,qiyemingcheng,yunguanmingcheng as zhengfuname,yunguanid as zhengfuid,areaname from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti')
and province = #{province}
and city = #{city}
and country = #{country}
)a INNER JOIN( select DISTINCT a.cid, a.company, a.date, a.uplinerate, chaosualarm, pilaotimes, case when a.uplinerate >= 60 then ROUND((a.uplinerate/100)*15,2) else 0.00 end as uplinerateScore, a.DriftPositionRate, case when a.DriftPositionRate > 0.10 then '0.00' else ROUND((10 - ((a.DriftPositionRate/100)*10)),4) end as DriftPositionRateScore, case when a.IntactPositionRate > 100 then '100.00' else IntactPositionRate end as IntactPositionRate, case when a.IntactPositionRate < 80 then '0.00' else ROUND((a.IntactPositionRate/100)*10,2) end as IntactPositionRateScore, a.QualifiedPositionRate, case when a.IntactPositionRate < 70 then '0.00' else ROUND((a.QualifiedPositionRate/100)*10,2) end as QualifiedPositionRateScore, case when chaosu = 0 then '10.00' when chaosu <= 100 then ROUND(((100-chaosu)/100)*10,2) when chaosu > 100 and chaosu < 100*2 then ROUND(((100*2-chaosu)/100)*10,2) end as chaosuScore, case when pilaotimes = 0 then '10.00' when pilaotimes <= 100 then ROUND(((100-pilaotimes)/100)*10,2) when pilaotimes > 100 and pilaotimes < 100*2 then ROUND(((100*2-pilaotimes)/100)*10,2) end as pilaotimesScore from( select DISTINCT cid, company, date, replace(ltrim(replace(vehiclecount,'0',' ')),' ','0') as vehiclecount, case when IFNULL(vehiclecount,0) = 0 or IFNULL(UpLineCount,0) = 0 then '0.00' else ROUND((UpLineCount*1.0/vehiclecount)*100,2) end as uplinerate, IFNULL(locateVehicleCount,0) as locateVehicleCount, case when IFNULL(vehiclecount,0) = 0 or IFNULL(locateVehicleCount,0) = 0 then '0.00' else ROUND((locateVehicleCount*1.0/vehiclecount)*100,2) end as locaterate, ROUND((DriftPositionRate)*100,2) as DriftPositionRate, ROUND(IntactPositionRate,2) as IntactPositionRate, case when ifnull(QualifiedPositionRate,'') = '' then '100.00' else ROUND(QualifiedPositionRate,2) end as QualifiedPositionRate, '100.00' as RuWangLv, '100.00' as CheckPostResponseRate, chaosu as chaosualarm, case when IFNULL(chaosu,0) = 0 then 0 else chaosu/replace(ltrim(replace(vehiclecount,'0',' ')),' ','0') end as chaosu, case when IFNULL(pilaotimes,0) = 0 then 0 else pilaotimes/replace(ltrim(replace(vehiclecount,'0',' ')),' ','0') end as pilaotimes from baobiao_alarmdailyreport where date = #{date} )a )b ON a.qiyeid = b.cid )a

select *, ROUND(IFNULL(uplinerateScore,0) +IFNULL(DriftPositionRateScore,0) +IFNULL(IntactPositionRateScore,0) +IFNULL(QualifiedPositionRateScore,0) +IFNULL(RuWangLvScore,0) +IFNULL(checkPostResponseRateScore,0) +IFNULL(chaosuScore,0) +IFNULL(pilaotimesScore,0),2) as countScore from( select DISTINCT a.zhengfuname, a.zhengfuid, a.areaname, a.qiyeid, a.qiyemingcheng, b.date, case when ifnull(b.uplinerateScore,0) > 5 then 5.00 else ifnull(b.uplinerateScore,0) end as uplinerateScore, case when ifnull(b.DriftPositionRateScore,0) > 10 then 10.00 else ifnull(b.DriftPositionRateScore,0) end as DriftPositionRateScore, case when ifnull(b.IntactPositionRateScore,0) > 15 then 15.00 else ifnull(b.IntactPositionRateScore,0) end as IntactPositionRateScore, case when ifnull(b.QualifiedPositionRateScore,0) > 15 then 15.00 else ifnull(b.QualifiedPositionRateScore,0) end as QualifiedPositionRateScore, case when ifnull(b.RuWangLvScore,0) > 5 then 5.00 else ifnull(b.RuWangLvScore,0) end as RuWangLvScore, case when ifnull(b.CheckPostResponseScore,0) > 10.00 then 10.00 else ifnull(b.CheckPostResponseScore,0) end as checkPostResponseRateScore, case when ifnull(b.chaosuScore,0) > 20 then 20.00 else ifnull(b.chaosuScore,0) end as chaosuScore, case when ifnull(b.pilaotimesScore,0) > 20 then 20.00 else ifnull(b.pilaotimesScore,0) end as pilaotimesScore, CONCAT(b.RuWangLv,'%') as RuWangLv, CONCAT(b.uplinerate,'%') as onlineRate, CONCAT(b.DriftPositionRate,'%') as DriftPositionRate, CONCAT(b.IntactPositionRate,'%') as IntactPositionRate, CONCAT(b.QualifiedPositionRate,'%') as QualifiedPositionRate, CONCAT(b.CheckPostResponseRate,'%') as CheckPostResponseRate from( select qiyeid,qiyemingcheng,yunguanmingcheng as zhengfuname,yunguanid as zhengfuid,areaname from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti')
and province = #{province}
and city = #{city}
and country = #{country}
)a INNER JOIN( select DISTINCT a.cid, a.company, a.date, a.uplinerate, ROUND((a.uplinerate/100)*5,2) as uplinerateScore, a.DriftPositionRate, case when a.DriftPositionRate > 0.05 then '0.00' else ROUND((10 - ((a.DriftPositionRate/100)*10)),4) end as DriftPositionRateScore, case when a.IntactPositionRate > 100 then '100.00' else IntactPositionRate end as IntactPositionRate, case when a.IntactPositionRate < 70 then '0.00' else ROUND((a.IntactPositionRate/100)*15,2) end as IntactPositionRateScore, a.QualifiedPositionRate, case when a.IntactPositionRate < 80 then '0.00' else ROUND((a.QualifiedPositionRate/100)*15,2) end as QualifiedPositionRateScore, a.RuWangLv, ROUND((a.RuWangLv/100)*5,2) as RuWangLvScore, IFNULL(a.CheckPostResponseRate,0) as CheckPostResponseRate, ROUND(a.CheckPostResponseRate*10,2) as CheckPostResponseScore, case when chaosu = 0 then '20.00' when chaosu <= 100 then ROUND(((100-chaosu)/100)*10,2) when chaosu > 100 and chaosu < 100*2 then ROUND(((100*2-chaosu)/100)*10,2) end as chaosuScore, case when pilaotimes = 0 then '20.00' when pilaotimes <= 100 then ROUND(((100-pilaotimes)/100)*10,2) when pilaotimes > 100 and pilaotimes < 100*2 then ROUND(((100*2-pilaotimes)/100)*10,2) end as pilaotimesScore from( select DISTINCT cid, company, date, replace(ltrim(replace(vehiclecount,'0',' ')),' ','0') as vehiclecount, case when IFNULL(vehiclecount,0) = 0 or IFNULL(UpLineCount,0) = 0 then '0.00' else ROUND((UpLineCount*1.0/vehiclecount)*100,2) end as uplinerate, IFNULL(locateVehicleCount,0) as locateVehicleCount, case when IFNULL(vehiclecount,0) = 0 or IFNULL(locateVehicleCount,0) = 0 then '0.00' else ROUND((locateVehicleCount*1.0/vehiclecount)*100,2) end as locaterate, ROUND((DriftPositionRate)*100,2) as DriftPositionRate, ROUND(IntactPositionRate,2) as IntactPositionRate, case when ifnull(QualifiedPositionRate,'') = '' then '100.00' else ROUND(QualifiedPositionRate,2) end as QualifiedPositionRate, '100.00' as RuWangLv, '100.00' as CheckPostResponseRate, chaosu as chaosualarm, case when IFNULL(chaosu,0) = 0 then 0 else chaosu/replace(ltrim(replace(vehiclecount,'0',' ')),' ','0') end as chaosu, case when IFNULL(pilaotimes,0) = 0 then 0 else pilaotimes/replace(ltrim(replace(vehiclecount,'0',' ')),' ','0') end as pilaotimes from baobiao_alarmdailyreport where date = #{date} )a )b ON a.qiyeid = b.cid )a

mapper.sql:
select DISTINCT *, ROUND(IFNULL(uplinerateScore,0) +IFNULL(DriftPositionRateScore,0) +IFNULL(IntactPositionRateScore,0) +IFNULL(QualifiedPositionRateScore,0) +IFNULL(CheckPostResponseRateScore,0),2) as countScore from( select DISTINCT a.zhengfuname, a.zhengfuid, a.areaname, b.operatorName as qiyemingcheng, b.date,
ROUND(AVG(case when ifnull(b.uplinerateScore,0) > 10 then 10.00 else b.uplinerateScore end),2) as uplinerateScore, ROUND(AVG(case when ifnull(b.DriftPositionRateScore,0) > 20 then 20.00 else b.DriftPositionRateScore end),2) as DriftPositionRateScore, ROUND(AVG(case when ifnull(b.IntactPositionRateScore,0) > 25 then 25.00 else b.IntactPositionRateScore end),2) as IntactPositionRateScore, ROUND(AVG(case when ifnull(b.QualifiedPositionRateScore,0) > 25 then 25.00 else b.QualifiedPositionRateScore end),2) as QualifiedPositionRateScore, ROUND(AVG(case when ifnull(b.CheckPostResponseRateScore,0) > 20 then 20.00 else b.CheckPostResponseRateScore end),2) as CheckPostResponseRateScore, CONCAT(ROUND(AVG(b.onlineRate),2),'%') as onlineRate, CONCAT(ROUND(AVG(b.DriftPositionRate),2),'%') as DriftPositionRate, CONCAT(ROUND(AVG(b.IntactPositionRate),2),'%') as IntactPositionRate, CONCAT(UND(AVROG(b.QualifiedPositionRate),2),'%') as QualifiedPositionRate, CONCAT(ROUND(AVG(b.CheckPostResponseRate),2),'%') as CheckPostResponseRate from( select qiyeid,qiyemingcheng,yunguanmingcheng as zhengfuname,yunguanid as zhengfuid,areaname from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti')
and province = #{province}
and city = #{city}
and country = #{country}
)a INNER JOIN( select DISTINCT a.deptId, a.operatorName, a.date,
ROUND((a.onlineRate/100)*10,2) as uplinerateScore, case
when a.CheckPostResponseRate < 90 then '0.00' else ROUND((a.CheckPostResponseRate/100)*20,2) end as CheckPostResponseRateScore, case
when a.IntactPositionRate < 70 then '0.00' else ROUND((a.IntactPositionRate/100)*25,2) end as IntactPositionRateScore, case
when a.IntactPositionRate < 80 then '0.00' else ROUND((a.QualifiedPositionRate/100)*25,2) end as QualifiedPositionRateScore, case
when a.DriftPositionRate > 0.05 then '0.00' else ROUND((20 - ((a.DriftPositionRate/100)*20)),4) end as DriftPositionRateScore, onlineRate, DriftPositionRate, IntactPositionRate, QualifiedPositionRate, CheckPostResponseRate from( select DISTINCT deptId, operatorName, date, ROUND((onlineRate),2) as onlineRate, ROUND((DriftPositionRate),2) as DriftPositionRate, ROUND(IntactPositionRate,2) as IntactPositionRate, case when ifnull(QualifiedPositionRate,'') = '' then '100.00' else ROUND(QualifiedPositionRate,2) end as QualifiedPositionRate, connTimes, case when IFNULL(connTimes,0) = 0 then '100.00' else (connTimes*1.0/1*(24*60*60))*100 end as CheckPostResponseRate from baobiao_operator_monitor where date = #{date} )a )b ON a.qiyeid = b.deptId where (IFNULL(operatorName,'') != '' and IFNULL(operatorName,'null') != 'null') GROUP BY a.zhengfuname, a.zhengfuid, a.areaname, b.operatorName, b.date )a

mapper.select:
select SUM(b.sxvehnum) as sxvehnum, SUM(b.vehnum) as vehnum, SUM(b.zaixiannum) as zaixiannum, SUM(b.vehnum - b.sxvehnum) as lxvehnum, CONCAT(ROUND(SUM(b.sxvehnum)*1.0/SUM(b.vehnum)*100,2),'%') as onlineRate from( select qiyeid,qiyemingcheng,yunguanmingcheng as zhengfuname,yunguanid as zhengfuid,areaname from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti')
and province = #{province}
and city = #{city}
and country = #{country}
)a INNER JOIN( select DeptID, SUM(case when LEFT(LastLocateTime,10) >= LEFT(NOW(),10) then 1 else 0 end) as sxvehnum, COUNT(VehId) as vehnum, SUM(case when Time >= DATE_SUB(NOW(), INTERVAL ${date} SECOND) then 1 else 0 end) as zaixiannum from tpvehdata a,anbiao_vehicle b where a.vehid = b.id and b.is_Deleted = 0 GROUP BY DeptID )b ON a.qiyeid = b.DeptID where 1=1
and a.qiyemingcheng like CONCAT('%',#{deptName},'%')

mapper.sql:
select DISTINCT a.zhengfuid, a.zhengfuname, a.qiyemingcheng as deptName, b.deptId, MAX(b.uploadedNum) as uploadedNum, IFNULL(SUM(b.finshNum),0) as finshNum from( select qiyeid,qiyemingcheng,yunguanmingcheng as zhengfuname,yunguanid as zhengfuid,areaname from baobiao_zhengfu_qiye where 1=1 and jigouleixing in ('qiye','geti')
and province = #{province}
and city = #{city}
and country = #{country}
)a INNER JOIN( select * from anbiao_safetyproductionfile_num where 1=1
and left(date,10) >= #{beginTime}
and left(date,10) <= #{endTime}
)b ON a.qiyeid = b.deptId GROUP BY a.zhengfuid, a.zhengfuname, a.qiyemingcheng, b.deptId
