mapper.select:
SELECT DISTINCT b.id, b.parent_id as parentId, b.dept_id as deptId, b.NAME, b.path, b.sort, b.tier, b.createtime, b.document_number as documentNumber, b.type, b.cumulative_visits, b.last_preview_time, b.is_muban as isMuban, b.muban_path as mubanPath, b.is_edit as isEdit, b.is_deleted as isDeleted, b.is_required as isRequired FROM anbiao_safetyproductionfile a, anbiao_safetyproductionfile b WHERE b.tier LIKE CONCAT(a.tier,'%')
AND b.dept_id = 1
AND b.dept_id = #{deptId}
AND b.is_deleted = 0 AND b.is_muban = 1
AND a.id = #{leixingid}
ORDER BY b.parent_id

SELECT DISTINCT b.id, b.parent_id as parentId, b.dept_id as deptId, b.NAME, b.path, b.sort, b.tier, b.createtime, b.document_number as documentNumber, b.type, b.cumulative_visits, b.last_preview_time, b.is_muban as isMuban, b.muban_path as mubanPath, b.is_edit as isEdit, b.is_deleted as isDeleted, b.is_required as isRequired FROM anbiao_safetyproductionfile a, anbiao_safetyproductionfile b WHERE b.tier LIKE CONCAT(a.tier,'%')
AND b.dept_id = 1
AND b.dept_id = #{deptId}
AND b.is_deleted = 0 AND b.type='文件夹' AND b.is_muban = 1
AND a.id = #{leixingid}
ORDER BY b.parent_id

SELECT id, parent_id, dept_id, NAME, path, sort, tier, createtime, document_number, type, cumulative_visits, last_preview_time, is_muban, muban_path, is_edit FROM anbiao_safetyproductionfile WHERE dept_id = #{deptId}
AND parent_id = #{parentId}
AND NAME like '%${Name}%'
AND is_deleted = 0
ORDER BY sort

select id,parent_id as parentId,`name` from anbiao_safetyproductionfile where parent_id = 0 and dept_id = #{deptId} and is_deleted = 0
and name = #{Name}
GROUP BY id,parent_id,`name`

mapper.update:
update anbiao_safetyproductionfile set parent_id = #{parentId},tier = #{tier},
   caozuoshijian = now(),caozuorenid = #{caozuorenid},caozuoren = #{caozuoren}
where id= #{id}

mapper.select:
select
    id,parent_id as parentId,`name`,tier
from
    anbiao_safetyproductionfile
where is_deleted = 0

    and dept_id = #{deptId}


    and name = #{Name}


    and id = #{Id}


    and parent_id = #{leixingid}


    and safeid = #{safeid}

GROUP BY id,parent_id,`name`

SELECT id, parent_id, dept_id, NAME FROM anbiao_safetyproductionfile WHERE dept_id = #{deptId}
and parent_id = #{Id}
and type = '文件夹' and is_deleted=0

SELECT parent_id, COUNT(DISTINCT parent_id) as num FROM anbiao_safetyproductionfile WHERE dept_id = #{deptId}
and parent_id = #{Id}
and type = '文件' and is_deleted=0 and is_muban != 1 and left(caozuoshijian,10) = left(now(),10) GROUP BY parent_id
