mapper.sql:
select vehicleColor, yunguanmingcheng, a.country, VehState, alarmtype, a.city, DeviceID, DeptID, shiyongxingzhi, vehicleNo, a.jigouleixing, a.province, areaName, yunguanid, vehicleID, qiyemingcheng as DeptName, VehState as zhuangtai, zaixian, d.area, longitude, latitude, Velocity, Angle, gpstime, c.yunyingshangmingcheng from( select * from baobiao_zhengfu_qiye where jigouleixing in('qiye','geti')
and province = #{province}
and city = #{city}
and country = #{country}
)a inner join( select Vehid as vehicleID, deptID, CASE WHEN ifnull(cheliangzhuangtai,0) = 0 THEN '在用' WHEN ifnull(cheliangzhuangtai,0) = 1 THEN '停用' END VehState, shiyongxingzhi, DeviceID, VeNumber as vehicleNo, VeColor as vehicleColor, IfNULL(Longitude,0) as longitude, IfNULL(Latitude,0) as latitude, Velocity, Angle, LastLocateTime as gpstime, '' as locationName, case when IfNULL(Locate,0) = 0 then '不定位' else '定位' end as LocalFlag, Systime, case when IfNULL(Alarm,0) = 0 then '未报警' else '报警' end as alarm, AlarmNote as alarmtype, case when Time >= DATE_SUB(NOW(), INTERVAL ${date} SECOND) then '在线' else '离线' end as zaixian, a.ACC, a.WiFiSignal, a.SatelliteLen from tpvehdata a )b on a.qiyeid = b.deptID left join anbiao_organization d on b.deptID = d.dept_id inner join( select distinct yunyingshangmingcheng,id from anbiao_vehicle where is_deleted = 0
and yunyingshangmingcheng = #{yunyingshang}
)c on b.vehicleID = c.id
where 1=1
