select * from blade_menu
where is_deleted = 0 and type=#{type}
   AND id IN ( SELECT menu_id FROM blade_post_menu WHERE type=0 and  post_id IN

    #{item}
 )

SELECT id, parent_id, `code`, `name`, alias, path, source, action, sort FROM blade_menu WHERE type=0 and id IN ( SELECT parent_id FROM blade_menu WHERE ( category = 2 AND id IN ( SELECT menu_id FROM blade_post_menu WHERE type=0 and post_id IN
#{item}
) ) )
UNION ALL
SELECT id, parent_id, `code`, `name`, alias, path, source, action, sort FROM blade_menu WHERE type=0 and ( category = 2 AND id IN ( SELECT menu_id FROM blade_post_menu WHERE type=0 and post_id IN
#{item} ) )

select id, parent_id, name as title, id as 'value', id as 'key' from blade_menu where is_deleted = 0 and type=#{type}
and id in ( select menu_id from blade_post_menu where type=0 and  post_id in

    #{item}
 )
or id in (
select parent_id from blade_menu where is_deleted = 0 and type=0
and id in ( select menu_id from blade_post_menu where type=0 and post_id in

    #{item}
 )
)

SELECT GROUP_CONCAT(r.role_alias) as alias, m.path FROM blade_role_menu rm LEFT JOIN blade_menu m ON rm.menu_id = m.id LEFT JOIN blade_role r ON rm.role_id = r.id WHERE rm.role_id IN
#{item}
AND m.path IS NOT NULL and rm.type=0 and m.type=#{type} GROUP BY m.path

select * from blade_menu where is_deleted = 0 and type = #{type} AND id IN ( SELECT menu_id FROM blade_post_menu WHERE type = #{type} and post_id IN

    #{item}
 )

