mapper.sql:
select unit_id, unitName, tradeKindName, count(DISTINCT courseId )as courseNumber, Max(totalNumber) AS totalNumber, count(DISTINCT student_id) as totalParticipateNumber, SUM(completedNumber) AS completedNumber, SUM(outstandingNumber) AS outstandingNumber, SUM(overdue_number) AS overdue_number from proc_unit_study_info where 1=1
AND unit_id = #{unitId, jdbcType=INTEGER}
AND courseId = #{courseId, jdbcType=INTEGER}
AND trade_kind_id = #{tradeKindId, jdbcType=INTEGER}
GROUP BY unit_id,unitName,tradeKindName

select * from proc_unit_study_deail_info
where 1=1

    AND unit_id = #{unitId, jdbcType=INTEGER}

SELECT unitName, realName, sex, identifyNumber, cellphone, '驾驶员' AS station, duration, concat( ROUND(( duration * 1.0 / 3600 ), 2 ), '小时' ) AS stuDuration, concat( ROUND(( zduration * 1.0 / 3600 ), 2 ), '小时' ) AS zduration, score, student_id AS studentId, '达标' AS result, photoUrl, signatrueimg, totalScores FROM( SELECT un.full_name AS unitName, d.student_id, b.realname, b.identify_number AS identifyNumber, b.cellphone, b.plate_number AS plateNumber, b.full_face_photo AS facePhoto, MAX( a.score ) AS score, c.duration, du.duration AS zduration, b.sex, b.address, pu.photoUrl, sg.signatrueimg, ce.total_scores AS totalScores FROM ( SELECT * FROM relation_student_course WHERE rel_unit_course_id = #{relUnitCourseId} ) d INNER JOIN base_student b ON d.student_id = b.id AND b.deleted = 0 INNER JOIN base_unit un ON un.id = b.unit_id AND un.deleted = 0 LEFT JOIN ( SELECT ruc.id, sum( bc.duration ) AS duration FROM relation_course_courseware rc INNER JOIN base_courseware bc ON bc.id = rc.courseware_id INNER JOIN relation_unit_course ruc ON rc.course_id = ruc.course_id WHERE bc.deleted = 0 AND ruc.id = #{relUnitCourseId} GROUP BY ruc.id ) du ON du.id = d.rel_unit_course_id LEFT JOIN ( SELECT * FROM biz_test_record WHERE rel_unit_course_id = #{relUnitCourseId} ) a ON a.student_id = b.id LEFT JOIN ( SELECT sum( duration ) AS duration, student_id FROM biz_study_record WHERE valid = 1 AND rel_unit_course_id = #{relUnitCourseId} GROUP BY student_id ) AS c ON b.id = c.student_id LEFT JOIN ( SELECT student_id, rel_unit_course_id, MAX( photo_url ) AS photoUrl FROM biz_snapshot GROUP BY student_id, rel_unit_course_id ) pu ON pu.student_id = d.student_id AND pu.rel_unit_course_id = a.rel_unit_course_id LEFT JOIN base_exam_signatrue sg ON sg.examid = a.id AND sg.studentid = d.student_id LEFT JOIN base_course_ext ce ON ce.rel_unit_course_id = d.rel_unit_course_id WHERE b.unit_id NOT IN ( 1, 2, 16 ) GROUP BY un.full_name, d.student_id, b.identify_number, b.cellphone, b.plate_number, b.full_face_photo, b.realname, c.duration, b.sex, b.address, pu.photoUrl, sg.signatrueimg, ce.total_scores ) list where 1=1
AND unitName = #{unitName}
AND realName like '%${realName}%'
